INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469', 2, 'Mhogo na Mtende', 'assets/topic/asb/25923.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_3', 3, NULL, 'assets/topic/asb/25919.png', 'Hapo zamani, Mhogo na Mtende waliishi kijijini Koowa. Kama marafiki, walitembeleana kila siku.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_4', 3, NULL, 'assets/topic/asb/25920.png', 'Katika mwaka mmoja, kulikuwa na ukosefu wa mvua. Mimea yote ilikauka. Watu wakakosa chakula.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_5', 3, NULL, 'assets/topic/asb/25921.png', 'Wlimkuta mwanamke mmoja wakamsalimu, "Hujambo?" Aliitikia kisha akwauliza, "Mnakwenda wapi?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_6', 3, NULL, 'assets/topic/asb/25922.png', '"Kazi gani mnayofanya?" Mwanamke yule aliuliza. Walijibu, "Tunaweza kuipa chakula familia yako na mifugo wako.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_7', 3, NULL, 'assets/topic/asb/25923.png', 'Mwanamke yule aliwapeleka nyumbani kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_8', 3, NULL, 'assets/topic/asb/25924.png', 'Alasiri moja, Mtende na Mhogo walibishana vikali. Mhogo alisema kuwa yeye alikuwa muhimu zaidi kuliko Mtende.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_9', 3, NULL, 'assets/topic/asb/25925.png', 'Mwanamke yule aliwasikia kutoka chumbani kwake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_10', 3, NULL, 'assets/topic/asb/25926.png', 'Mhogo alikuwa wa kwanza kuzungumza. "Mimi ni muhimu zaidi kuliko Mtende. Ninazaa mihogo mnayotumia kutengeneza fufu na unga wa mihogo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_11', 3, NULL, 'assets/topic/asb/25927.png', '"Watu hupanda mashina yangu. Mifugo wenu hula majani na maganda yangu. Je, Mtende hufanya nini?" Mhogo aliuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_12', 3, NULL, 'assets/topic/asb/25928.png', 'Mtende alicheka, akatikisa kichwa chake kisha akasema, "Mwanamke wee, unaikumbuka supu ya tende unayofurahia sana? Hutoka kwa nani?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_13', 3, NULL, 'assets/topic/asb/25929.png', '"Mimi hutoa mafuta yanayotumiwa kukaanga samaki na nyama. Je, utawezaje kutayarisha kitoweo na mchuzi bila mafuta?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_14', 3, NULL, 'assets/topic/asb/25930.png', '"Zaidi ya hayo, matawi yangu huezeka paa za vyumba vyenu na vibanda vya kupumzikia. Mnaburudika baada ya kazi kwa kuinywa kosha ninayotoa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_15', 3, NULL, 'assets/topic/asb/25931.png', '"Isitoshe, fagio zinazotumiwa kufagia vyumba na uwanja wa nyumba zenu zinatoka kwangu," Mtende alimaliza kusema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_16', 3, NULL, 'assets/topic/asb/25932.png', '"Hmmm!" Mwanamke alizusha pumzi. "Ni sawa marafiki, nimewasikia. Nitaitatua shida hii."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_17', 3, NULL, 'assets/topic/asb/25933.png', '"Ninyi nyote ni viumbe muhimu sana kwangu. Pamoja, mnatengeneza mlo mtamu wa fufu na supu ya tende!" mwanamke akasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_18', 3, NULL, 'assets/topic/asb/25934.png', 'Mwanamke yule alitayarisha supu ya tende pamoja na fufu kutoka kwenye mihogo. Aliwaalika marafiki zake kula naye. Walifurahia chakula hicho sana.
