INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140', 2, 'Mke Mti', 'assets/topic/asb/2580.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_3', 3, NULL, 'assets/topic/asb/2584.png', 'Hapo zamani za kale, paliishi mzee aliyekuwa tajiri wa kila kitu. Alikuwa na shamba kubwa lenye rutuba,  ng''ombe, na mbuzi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_4', 3, NULL, 'assets/topic/asb/2585.png', 'Siku moja alipokuwa chini ya mti, alipata wazo zuri sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_5', 3, NULL, 'assets/topic/asb/2583.png', 'Alianza kazi ya kumchonga mke kutoka kwa gogo la mti. Alipomaliza kuchonga sanamu, aliigusa macho nayo yakaanza kuona.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_6', 3, NULL, 'assets/topic/asb/2586.png', 'Akawa mwanamke mrembo zaidi ya wote aliokuwa ameona.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_7', 3, NULL, 'assets/topic/asb/2587.png', 'Yule mume alimvisha kimori, shanga na mkufu wa kichwani, ishara kwamba alikuwa ameolewa. Kisha alimjengea nyumba akitumia matope na nyasi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_8', 3, NULL, 'assets/topic/asb/2588.png', 'Lakini kabla ya kipindi kirefu, vijana kutoka kijiji jirani walianza kusema, "Inawezekanaje mzee kama huyu kumwoa msichana mrembo namna hii!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_9', 3, NULL, 'assets/topic/asb/2589.png', 'Mkewe alipoibiwa, mzee huyo alihuzunika sana. Alihisi kwamba hangeishi bila mkewe. Akafikiria, "Labda nikipata kitu chochote kutoka kwake, nitapata faraja."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_10', 3, NULL, 'assets/topic/asb/2590.png', 'Walipomwona walimwimbia wimbo mtamu:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_11', 3, NULL, 'assets/topic/asb/2591.png', 'Aliwapa Kimori chake wakaruka, juu ya milima, juu ya mito, hadi wakarudi kwa mumewe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_12', 3, NULL, 'assets/topic/asb/2592.png', 'Faraja hiyo haikudumu. Aliwatuma wale njiwa tena wakamwimbie mkewe:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_13', 3, NULL, 'assets/topic/asb/2581.png', 'Aliuchukua ule mkufu akapata faraja ya mke wake aliyekuwa katika kijiji cha watu wengine. Baada ya muda mfupi aliwaita njiwa tena akawatuma kwa mkewe tena wakamwimbie:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_14', 3, NULL, 'assets/topic/asb/2582.png', 'Walienda tena mara ya tatu wakatua mabegani mwake. Walipokuwa wakiimba, kila njiwa alikuwa akidona na kumng''oa jicho.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_15', 3, NULL, 'assets/topic/asb/2593.png', 'Mumewe alilisukuma lile gogo la sanamu mtoni. Akalisimamisha upande wa mizizi ukiwa majini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3140_16', 3, NULL, 'assets/topic/asb/2580.png', 'Na wakati upepo uvumapo, majani yake hushusha pumzi kama vile mwanamke hushusha pumzi anapotamani kumwona mumewe.', NULL);
