INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079', 2, 'Mke-mwenza mwenye wivu', 'assets/topic/asb/25521.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_3', 3, NULL, 'assets/topic/asb/25510.png', 'Hapo kale kulikuwa na wake wenza wawili. Waliishi katika nyumba moja na mume wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_4', 3, NULL, 'assets/topic/asb/25511.png', 'Kwa muda mrefu hakuna mke aliyejaliwa kupata watoto. Hatimaye, mke wa kwanza alishika mimba.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_5', 3, NULL, 'assets/topic/asb/25512.png', 'Mke wa pili aliwaonea wivu mke wa kwanza na mtoto wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_6', 3, NULL, 'assets/topic/asb/25513.png', 'Nyakati hizo, watu hawakuzoea kupika chakula.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_7', 3, NULL, 'assets/topic/asb/25514.png', 'Mke wa pili alisema, "Nitakipika chakula ili nimdhuru mke-mwenzangu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_8', 3, NULL, 'assets/topic/asb/25515.png', 'Basi alianza kumpikia mke mwenziwe chakula.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_9', 3, NULL, 'assets/topic/asb/25516.png', 'Mume wao aliporudi nyumbani, alimkuta mtoto mchanga na mke wa kwanza mwenye afya. Pia alitambua kuwa chakula kilikuwa kikipikwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_10', 3, NULL, 'assets/topic/asb/25517.png', 'Mume huyo alishangazwa na upikaji wa chakula. Akauliza, "Nani aliwafunza kupika chakula?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_11', 3, NULL, 'assets/topic/asb/25518.png', '"Ninamwonea wivu kwa sababu alimpata mtoto ilhali mimi sina. Nina wasiwasi kuwa utanifukuza ubaki naye."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_12', 3, NULL, 'assets/topic/asb/25519.png', 'Mumewe alikasirika kisha akamuonya, "Acha kuwa na fikira hizo mbaya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_13', 3, NULL, 'assets/topic/asb/25520.png', 'Kisha yule mume aliwaleta wakeze pamoja.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22079_14', 3, NULL, 'assets/topic/asb/25521.png', 'Walikubali kuendelea kupika chakula kwa sababu kiliwafanya kuwa na afya nzuri.
