INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860', 2, 'Mkeka wa Akai', 'assets/topic/asb/6007.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_3', 3, NULL, 'assets/topic/asb/5845.png', 'Akai alipokuwa mdogo, mamake alikuwa na mazoea ya kumlaza kwenye mkeka maridadi. Mkeka huo ulitengezwa na shangaziye Akai kwa majani ya mtende.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_4', 3, NULL, 'assets/topic/asb/5846.png', 'Mkeka wa Akai ulikuwa na rangi za waridi, bluu na kijani kibichi. Ulikuwa tofauti na mikeka mingine aliyokuwa nayo mamake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_5', 3, NULL, 'assets/topic/asb/5847.png', 'Makazi ya kina Akai yalikuwa katika sehemu kavu,  iliyojaa mawe na yenye joto. Kulikuwa na nge, buibui na nyoka pia. Akai hakuwahi kuumwa na viumbe hao hatari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_6', 3, NULL, 'assets/topic/asb/5848.png', '"Huu mkeka wako wa kipekee hukukinga na hatari." Mamake alimwambia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_7', 3, NULL, 'assets/topic/asb/5996.png', 'Akai alikuwa mtoto mwerevu. Alijua mahali kilipokuwa kisima cha karibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_8', 3, NULL, 'assets/topic/asb/5997.png', 'Vile vile alijua mahali ilipokuwa manyatta ya nyanyake. Mara kwa mara alienda kwa nyanyake kunywa maziwa ya ngamia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_9', 3, NULL, 'assets/topic/asb/5999.png', 'Siku moja, Akai aliondoka nyumbani kwenda kwa nyanyake. Alipotelea milimani, akaogopa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_10', 3, NULL, 'assets/topic/asb/6000.png', 'Aliamua kuketi chini ya mti wa edome kusubiri usaidizi. Muda mfupi baadaye, alilala akaota ndoto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_11', 3, NULL, 'assets/topic/asb/6001.png', 'Ndotoni, Akai alikuwa amelala kwenye mkeka wake kipekee. Bi kizee aliyefanana na nyanyake alikuwa akimchunga. Bi kizee huyo alitabasamu na kumpa maziwa. Akai alipounyoosha mkono wake kuyapokea, aliamka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_12', 3, NULL, 'assets/topic/asb/6002.png', 'Akai aliyafungua macho yake pole pole. Alitazama juu akamwona ndege mdogo wa rangi ya bluu kwenye tawi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_13', 3, NULL, 'assets/topic/asb/6003.png', 'Akai aliposimama, yule ndege alipapatisha mbawa zake kisha akamwongoza. Akai alimfuata.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_14', 3, NULL, 'assets/topic/asb/6004.png', 'Akai alifika mahali penye njia panda. Yule ndege akakitupa kipande cha mkeka kilichofanana na ule wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_15', 3, NULL, 'assets/topic/asb/6005.png', 'Akai alikichukuwa kipande hicho akakitazama kwa makini. Aliziona nyayo alizozifahamu kuwa za mamake. Baadaye, alikiona kisima walikochota maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_16', 3, NULL, 'assets/topic/asb/6006.png', 'Akai hatimaye alifika nyumbani. Watu wote wa familia yake waliimba na kucheza kumkaribisha nyumbani. Walimchinjia mbuzi, wakachoma nyama na kusherehekea kurudi salama kwa mtoto wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7860_17', 3, NULL, 'assets/topic/asb/6007.png', 'Akai aliukalia mkeka wake wa kipekee akila kipande kikubwa cha nyama. Akai alifurahi sana.', NULL);
