INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530', 2, 'Mkoba uzungumzao', 'assets/topic/asb/1461.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_3', 3, NULL, 'assets/topic/asb/1461.png', 'Hapo zamani za kale aliishi jitu jike lililoitwa Motio. Siku moja lilipokuwa likitembea kando mwa ziwa liliwaona wasichana watatu, Lisa, Vera na Elsa. Likawauliza, "Ni nani kati yenu anayependwa na wazazi wote wawili?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_4', 3, NULL, 'assets/topic/asb/1462.png', 'Lisa alijibu kwanza, akasema, "Mamangu hunipenda zaidi." Vera naye akasema, "Babangu hunipenda zaidi." Elsa akasema, "Baba na mama wananipenda sawa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_5', 3, NULL, 'assets/topic/asb/1463.png', 'Motio alimwambia Elsa amfuate akamsaidie kuliinua tita la kuni amwekee mgongoni. Elsa alimfuata Motio bila kuogopa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_6', 3, NULL, 'assets/topic/asb/1465.png', 'Walipotembea hatua chache, Elsa alimuuliza Motio, "Zi wapi kuni zako nikusaidie?" Motio akajibu, "Ziko karibu na ile miti."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_7', 3, NULL, 'assets/topic/asb/1466.png', 'Walipofika hapo, Elsa akamuuliza Motio tena, "Zi wapi kuni zako?" Motio akajibu, "Haziko mbali kutoka hapa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_8', 3, NULL, 'assets/topic/asb/1467.png', 'Walipokuwa wakitembea, walikutana na mwanamume. Mwanamume huyo alimuuliza Motio,  "Mtoto wako anaitwaje?" Motio akajibu, "Anaitwa Mkoba Uzungumzao."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_9', 3, NULL, 'assets/topic/asb/1468.png', 'Elsa aliimba kwa sauti, "Mimi kamwe si mkoba.  Mkoba hauongei. Ninaitwa Elsa. Lo! Nyumbani kwetu, Mama nimpendaye, Baba nimpendaye, Kata nilinywealo maziwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2530_10', 3, NULL, 'assets/topic/asb/1469.png', 'Mwanamume huyo alipousikia wimbo wa Elsa,  alimhurumia, akamuuliza amwelezee juu ya wazazi wake. Alimnusuru kutoka kwa Motio na kumrudisha kwa babake na mamake. Wazazi wake walimshukuru mwanamume huyo na wakaishi kwa furaha na mtoto wao.', NULL);
