INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026', 2, 'Mlinzi na chekechea yake', 'assets/topic/asb/12997.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_3', 3, NULL, 'assets/topic/asb/12979.png', 'Hadithi hii anamhusu Mlinzi na chekechea yake ya wanyama mayatima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_4', 3, NULL, 'assets/topic/asb/12980.png', 'Agosti ulikuwa mwezi wenye shughuli nyingi. Mlinzi na wasaidizi wake waliwapokea wanyama wengi katika chekechea yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_5', 3, NULL, 'assets/topic/asb/12981.png', 'Mkite alikuwa wa kwanza kufika. Alikuwa mtoto wa tembo mwenye umri wa mwaka mmoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_6', 3, NULL, 'assets/topic/asb/12982.png', 'Mlinzi na wasaidizi wake walimhurumia Mkite sana. Alikuwa amekonda na mwenye huzuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_7', 3, NULL, 'assets/topic/asb/12983.png', 'Siku chache baadaye, afya ya Mkite iliimarika. Alicheza na wanyama wengine aliowakuta pale.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_8', 3, NULL, 'assets/topic/asb/12984.png', 'Siku nyingine katika mwezi huo wa Agosti, helikopta ilitua mbele ya chekechea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_9', 3, NULL, 'assets/topic/asb/12985.png', 'Walimpokea yatima wa pili katika mwezi huo. Alikuwa mtoto mwingine wa tembo. Alikuwa amefunikwa kwa  blanketi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_10', 3, NULL, 'assets/topic/asb/12986.png', 'Yatima huyo aliitwa Ndiwa. Alikuwa na umri wa siku tano. Mlinzi aliitumia chupa kumnywesha maziwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_11', 3, NULL, 'assets/topic/asb/12987.png', 'Yatima wa tatu kufika mwezi huo alikuwa Malea, kifaru. Malea alikwa na umri wa miezi sita.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_12', 3, NULL, 'assets/topic/asb/12988.png', 'Malea na Enkare walikuwa marafiki wakubwa. Enkare alikuwa nyati mtoto. Walienda kila mahali pamoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_13', 3, NULL, 'assets/topic/asb/12989.png', 'Usiku mmoja, Mlinzi na wasaidizi wake waliamshwa. Walimpokea twiga mtoto aliyepewa jina Ambia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_14', 3, NULL, 'assets/topic/asb/12990.png', 'Ambia alikuwa na umri wa miezi mitano. Alihisi njaa na uchovu baada ya safari ndefu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_15', 3, NULL, 'assets/topic/asb/12991.png', 'Kwa sababu ya urefu wake, Mlinzi aliupanda mti kumlisha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_16', 3, NULL, 'assets/topic/asb/12992.png', 'Abei na Moit, swara mapacha, walifika katikati ya mwezi wa Agosti. Walikuwa wamezaliwa saa chache tu. Walikuwa wagonjwa sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_17', 3, NULL, 'assets/topic/asb/12994.png', 'Walihuzunika Abei alipoaga dunia wiki moja baadaye. Mlinzi na wasaidizi wake walimwokoa Moit.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_18', 3, NULL, 'assets/topic/asb/12997.png', 'Mayatima wa mwisho kufika mwezi huo wa Agosti walikuwa Kopi, Kepi na Keji. Walikuwa wana wa simba wenye umri wa wiki mbili. Walikuwa na kiu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_19', 3, NULL, 'assets/topic/asb/12995.png', 'Mlinzi na wasaidizi wake waliwapenda wanyama wote hata wale watukutu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_20', 3, NULL, 'assets/topic/asb/12996.png', 'Mtukutu kwa wote alikuwa Lobolia. Alizoea kuichukua miwani ya Mlinzi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_21', 3, NULL, 'assets/topic/asb/12998.png', 'Wafanyakazi wa chekechea wanafanya bidii kuwatunza wanyama hao. Watakapoweza kujitegemea, watarudi porini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13026_22', 3, NULL, NULL, 'MAZOEZI:   Soma tena hadithi kisha ujibu maswali haya. 1. Wanyama wangapi walifika katika chekechea mwezi Agosti? 2. Nani alifika kwanza? 3. Mnyama huyu alikuwa na umri gani? 4. Nani alifika mwisho? Wanyama hao walikuwa na umri gani? 5. Wanyama gani waliokuwa na umri wa chini zaidi? 6. Walikuwa na umri gani? 7. Mnyama yupi aliyefika mwezi Agosti aliyekuwa mzee kwa wote?', NULL);
