INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771', 2, 'Mojo na Kobe', 'assets/topic/asb/26787.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_3', 3, NULL, 'assets/topic/asb/26785.png', 'Hapo zamani, ndege aliyeitwa Mojo aliishi katika ufalme wa Baro. Kobe alikuwa rafikiye.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_4', 3, NULL, 'assets/topic/asb/26786.png', 'Siku moja, Mfalme Simba alimwita Mojo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_5', 3, NULL, 'assets/topic/asb/26787.png', 'Kobe aliposikia hivyo, alimwonea wivu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_6', 3, NULL, 'assets/topic/asb/26788.png', 'Mojo aliogopa. Siku iliyofuata, alienda kwa jamaa yake  Jimbi akamwambia alivyoambiwa na Kobe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_7', 3, NULL, 'assets/topic/asb/26789.png', '"Nitakutayarishia mlo mtamu usiku wa leo," Kobe alimweleza Mojo alipokutana naye baadaye.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_8', 3, NULL, 'assets/topic/asb/26790.png', 'Mojo alipokuwa akistarehe, Kobe alimtupia wavu akamnasa! Kisha alienda nje kutafuta kuni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_9', 3, NULL, 'assets/topic/asb/26791.png', 'Jimbi alikuwa karibu na nyumba ya Kobe. Alimsikia Mojo akipiga kelele. Aliita, "Mojo? Mojo?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24771_10', 3, NULL, 'assets/topic/asb/26792.png', 'Jimbi alimwokoa Mojo kutoka kwenye wavu.
