INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419', 2, 'Moto, Maji, Ukweli na Uongo', 'assets/topic/asb/18348.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_3', 3, NULL, 'assets/topic/asb/18347.png', 'Zamani kulikuwa na marafiki wanne.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_4', 3, NULL, 'assets/topic/asb/18348.png', 'Uongo hakufurahi. Alitaka kuuvunja urafiki wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_5', 3, NULL, 'assets/topic/asb/18349.png', 'Siku moja Uongo alisema, "Tuitafute nchi huru ili kila mmoja wetu amiliki ufalme wake."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_6', 3, NULL, 'assets/topic/asb/18350.png', 'Uongo alimwambia Maji, "Moto ni adui yetu mkubwa. Yeye huunguza kila kitu. Tutafute ufalme ambako Moto hatakuweko."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_7', 3, NULL, 'assets/topic/asb/18351.png', 'Maji akauliza, "Tutafanyaje?" Uongo akamjibu,  "Ni wazi kwamba tutamuua. Ni wewe tu uliye na uwezo wa kufanya hivyo. Atakapokuwa ameketi chini,  jimwagilie kwake umharibu!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_8', 3, NULL, 'assets/topic/asb/18352.png', 'Maji alisema, "Nikifanya hivyo nitamwagika duniani. Sitaweza kujikusanya na kuwa pamoja tena." Uongo alikanusha, "Hilo si tatizo. Nitayaweka mawe chini yakuzuie kuenee kila mahali. Kisha nitakukusanya tena." Uongo alifanya hivyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_9', 3, NULL, 'assets/topic/asb/18353.png', 'Moto alipokuwa ameketi chini, Maji alijimwaga kwake bila Moto kujua. Kupitia njia hiyo, Uongo aliweza kumwangamiza Moto.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_10', 3, NULL, 'assets/topic/asb/18354.png', 'Baada ya muda, Uongo alimwambia Maji, "Hebu keti juu ya ukingo huu ili uyafurahie mandhari haya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_11', 3, NULL, 'assets/topic/asb/18355.png', 'Maji alipokuwa ameketi chini, Uongo aliyaondosha mawe yaliyomzingira.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_12', 3, NULL, 'assets/topic/asb/18356.png', 'Sasa Uongo alilazimika kumwangamiza Ukweli.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_13', 3, NULL, 'assets/topic/asb/18357.png', 'Kisha Uongo akalisukuma jiwe kubwa kutoka juu kumwangamiza Ukweli.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_14', 3, NULL, 'assets/topic/asb/18358.png', 'Jiwe lilipoporomoka chini, almasi, dhahabu na vito tofauti vya thamani vilianguka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_15', 3, NULL, 'assets/topic/asb/18359.png', 'Uongo alishuka chini kutazama mwili wa Ukweli. Badala yake, aliviona vito vya thamani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_16', 3, NULL, 'assets/topic/asb/18360.png', 'Ukweli alijibu, "Jiwe liliponiangukia, vito hivi vilimwagika." Uongo pia alitaka vito hivyo vya thamani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_17', 3, NULL, 'assets/topic/asb/18361.png', 'Basi, Ukweli akapanda juu ya mlima akalisukuma chini lili jiwe kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18419_18', 3, NULL, 'assets/topic/asb/18362.png', 'Jiwe hilo lilimwangukia Uongo kichwani. Akafa papo hapo.', NULL);
