INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994', 2, 'Msichana mwenye titi moja', 'assets/topic/asb/2673.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994_3', 3, NULL, 'assets/topic/asb/2673.png', 'Hapo kale palikuwa na mtu aliyeishi na mkewe aliyempenda sana. Siku moja alitembea msituni. Njiani alikutana na msichana mmoja mwembamba na mrembo mno. Alimpenda papo hapo. Msichana vilevile alimkubali mara moja. Lakini, msichana yule alimwambia kuwa alikuwa na titi moja tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994_4', 3, NULL, 'assets/topic/asb/2674.png', 'Msichana yule alimwambia mwanamme huyo aende nyumbani, akate titi moja la mkwewe amletee. Hivyo yeye angekuwa na matiti mawili nao wangeoana. Mwanamme alikimbia kwa kasi, akachukua kisu,  akamrukia mkewe mpendwa na kukata titi lake mara moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994_5', 3, NULL, 'assets/topic/asb/2675.png', 'Alifanya haraka kurudi msituni alikomwacha mpenzi wake mpya. Lakini hakuwapo. Aliliita jina lake kwa sauti, lakini ilikuwa kimya tu. Kisha alisikia sauti ikisema, "Kwa nini unaita? Kwani hujui kwamba uko katika msitu wa mapepo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994_6', 3, NULL, 'assets/topic/asb/2676.png', 'Mtu yule aliposikia hivyo, alikimbia kama risasi kurudi nyumbani. Alimpata mkewe tayari akiwa amefariki. Alihuzunika sana kwa ajili ya kitendo chake. Naye aliona heri afe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2994_7', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309', 2, 'Msitu wenye nyoka', 'assets/topic/asb/10763.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_3', 3, NULL, 'assets/topic/asb/10763.png', 'Kijiji cha Sinyaire kimezungukwa na milima na misitu. Nyoka wengi huishi ndani ya misitu hiyo. Nyoka aliyekuwa mkubwa zaidi aliitwa Unyalego na wanakijiji. Nyoka huyo aliwameza mbuzi na kondoo wakiwa wazima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_4', 3, NULL, 'assets/topic/asb/10765.png', 'Siku moja, Apiyo, Ajoh na Atieno walipokuwa wakienda msituni kutafuta kuni, bibi aliwaonya, "Jihadharini na Unyalego. Msichukue chochote kutoka msituni ila kuni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_5', 3, NULL, 'assets/topic/asb/10766.png', 'Wasichana hao walifunganya maziwa, ndizi, viazi vitamu na maji. Wakachukua kamba za kufungia kuni na panga kali. Wakaondoka kwenda msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_6', 3, NULL, 'assets/topic/asb/10767.png', 'Walipokuwa njiani, Ajoh, Apiyo na Atieno waliongea na kucheka. Ghafla, Apiyo alisema, "Kimya! Tunakaribia anakoishi Unyalego."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_7', 3, NULL, 'assets/topic/asb/10769.png', 'Ajoh hakuzingatia onyo la nyanyake. Atieno aliwaita,  "Tazameni, hapa kuna meno ya Unyalego ya dhahabu. Tuyachukue?" Apiyo aliwakumbusha,  "Msisahau kwamba bibi alituonya tusichukue chochote kutoka msituni ila kuni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_8', 3, NULL, 'assets/topic/asb/10770.png', 'Ajoh akajibu, "Simwamini bibi. Nadhani alisema hivyo ili atuzuie kuchukua tunachotaka. Najua anatuonea wivu. Mimi nitayachukua hayo meno ya dhahabu." Dada zake walikuwa na wasiwasi wakamwonya Ajoh lakini, hakujali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_9', 3, NULL, 'assets/topic/asb/10771.png', 'Baadaye, Unyalego alikuja kutafuta meno yake. Alikasirika alipoyakosa. Aliwasikia Ajoh, Apiyo na Atieno wakizungumza na kucheka. Akajificha kando ya njia ambayo wasichana wale wangepitia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_10', 3, NULL, 'assets/topic/asb/10772.png', 'Walipokwisha kuzikusnaya kuni, walizibeba vichwani. Ghafla, wakasikia "Hzzzzzzzzz!" Walimwona Unyalego akiwa kinywa wazi, tayari kuwameza. Waliogopa sana wakajaribu kujificha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_11', 3, NULL, 'assets/topic/asb/10773.png', 'Unyalego akawauliza, "Ni nani aliyeyachukua meno yangu ya dhahabu?" Walipokosa kumjibu, Unyalego akaamua kuwadadisi wasichana hao. Akasema,  "Ninataka muimbe wimbo. Yule atakayeimba bila kukosea, atakuwa hana hatia na ataenda nyumbani na kuni zake. Atakayekosea, ndiye aliye na hatia na atakuwa chakula changu kwa siku tatu zijazo!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_12', 3, NULL, 'assets/topic/asb/10774.png', 'Apiyo aliimba kwanza kwa sauti nyororo: Sio mimi,  sio mimi niliyeiba meno, aliyeyaiba yuko hapo nyuma aaaah. Kisha Atieno akaimba: Sio mimi, sio mimi niliyeiba meno, aliyeyaiba yuko hapo nyuma aaaah.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_13', 3, NULL, 'assets/topic/asb/10775.png', 'Mwishowe, ilikuwa zamu ya Ajoh. Kwa uwoga alisongea mbele na kuanza kuimba: S.i.o mi-mi, s.i.o.  Aliimba vibaya akashindwa kuyatamka maneno sawa. Unyalego akamkemea, "Mbona huimbi vizuri kama wengine?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13309_14', 3, NULL, 'assets/topic/asb/10776.png', 'Papo hapo, Unyalego akammeza Ajoh. Atieno na Apiyo walikimbia kuwaeleza wanakijiji yaliyotokea.', NULL);
