INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861', 2, 'Mtoto aliyeleta Amani', 'assets/topic/asb/14590.png', 'Translators without Borders and Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_3', 3, NULL, 'assets/topic/asb/14580.png', 'Akadeli alimbeba mtoto wake. Alikwenda msituni kuchuma matunda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_4', 3, NULL, 'assets/topic/asb/14581.png', 'Alipata mti uliojaa matunda mabivu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_5', 3, NULL, 'assets/topic/asb/14582.png', 'Akadeli alimlaza mtoto wake chini akapanda mti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_6', 3, NULL, 'assets/topic/asb/14583.png', 'Jasusi mmoja kutoka kijiji jirani alipitia pale akamwona mtoto. Kwa mshangao,  akajiuliza,  "Mamake mtoto huyu yuko wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_7', 3, NULL, 'assets/topic/asb/14584.png', 'Jasusi yule alipochutama,  sauti ya mikufu aliyoivaa shingoni mwake,  ilimwamsha mtoto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_8', 3, NULL, 'assets/topic/asb/14585.png', 'Alimwacha mtoto aichezee mikufu hiyo.  Mtoto alicheza na kucheka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_9', 3, NULL, 'assets/topic/asb/14586.png', 'Akadeli alitazama chini aone kilichomchekesha mtoto. Akamwona jasusi amechutama karibu na mtoto wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_10', 3, NULL, 'assets/topic/asb/14587.png', 'Hofu ilimwandama,  akauangusha mfuko wake uliojaa matunda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_11', 3, NULL, 'assets/topic/asb/14588.png', 'Jasusi yule alitazama juu akasema,  "Usiogope. Ninacheza tu na mtoto wako mrembo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_12', 3, NULL, 'assets/topic/asb/14589.png', 'Polepole, Akadeli akashuka kutoka mtini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_13', 3, NULL, 'assets/topic/asb/14590.png', 'Jasusi akampa mtoto mkufu mmoja, akisema, "Hii ni zawadi yako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15861_14', 3, NULL, 'assets/topic/asb/14591.png', 'Akamwambia Akadeli, "Mchukue mtoto na mkoba wako uende nyumbani. Mwambie mume wako ahamie sehemu yenye amani zaidi. Mtoto wako amenifanya nibadili nia."', NULL);
