INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865', 2, 'Mtoto Punda', 'assets/topic/asb/14980.png', 'Translators without Borders and Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_3', 3, NULL, 'assets/topic/asb/14972.png', 'Siku moja, msichana mdogo aliona umbo la ajabu kwa umbali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_4', 3, NULL, 'assets/topic/asb/14973.png', 'Umbo hilo lilipokaribia, aligundua kwamba lilikuwa mwanamke mja mzito.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_5', 3, NULL, 'assets/topic/asb/14974.png', 'Kwa ushupavu, msichana huyo alimkaribia yule mwanamke mja mzito.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_6', 3, NULL, 'assets/topic/asb/14976.png', 'Baadaye, mama huyo alikuwa tayari kujifungua.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_7', 3, NULL, 'assets/topic/asb/14977.png', 'Mama alijifungua. Majirani walipomwona mtoto, wote walirudi nyuma kwa mshtuko.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_8', 3, NULL, 'assets/topic/asb/14978.png', 'Wakaanza kugombana. "Tulikubali kuwa tutamlinda huyu mama hadi atakapojifungua, na hivyo ndivyo tutakavyofanya." Kiongozi wao alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_9', 3, NULL, 'assets/topic/asb/14979.png', 'Mama yule alijikuta peke yake tena. Akajiuliza,  "Nitafanyaje na mtoto huyu mwenye kunifedhehesha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_10', 3, NULL, 'assets/topic/asb/14980.png', 'Hatimaye, alikubali kuwa huyo ni mtoto wake na yeye ndiye mamake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_11', 3, NULL, 'assets/topic/asb/14981.png', 'Lakini, alikua, akakua, hadi mamake akashindwa kumbeba mgongoni. Mamake mara nyingi alichoka na kuvunjika moyo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_12', 3, NULL, 'assets/topic/asb/14982.png', 'Mtoto punda alikasirika akachanganyikiwa. Hangeweza kufanya lolote kama binadamu. Hakufanana na huyu wala yule.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_13', 3, NULL, 'assets/topic/asb/14983.png', 'Mtoto punda aliona aibu. Akakimbilia mbali kadiri alivyoweza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_14', 3, NULL, 'assets/topic/asb/14984.png', 'Aliendelea kukimbia hadi usiku alipotambua kuwa amepotea. "Hi ho?" Alinong''ona gizani. "Hi ho?" Mwangwi ukamrudia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_15', 3, NULL, 'assets/topic/asb/14985.png', 'Kulipokucha, mtoto punda aliamka na kumkuta mzee wa ajabu akimtazama. Naye pia akamtazama mzee machoni kwa hisia za matumaini. Mzee akasema,  "Njoo twende kwangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_16', 3, NULL, 'assets/topic/asb/14986.png', 'Mtoto punda alikubali mwaliko wa mzee. Mzee alimfundisha njia nyingi za maisha. Mtoto punda alijifunza mengi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_17', 3, NULL, 'assets/topic/asb/14987.png', 'Asubuhi moja, mzee alimwomba mtoto punda ambebe hadi kilele cha mlima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_18', 3, NULL, 'assets/topic/asb/14988.png', 'Walipofika juu mawinguni, walilala.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_19', 3, NULL, 'assets/topic/asb/14989.png', 'Alipoamka, mawingu yalikuwa yametoweka na mzee rafikiye alikuwa hayupo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_20', 3, NULL, 'assets/topic/asb/14990.png', 'Hatimaye, mtoto punda alifahamu atakavyofanya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_21', 3, NULL, 'assets/topic/asb/14991.png', 'Mtoto punda alirudi nyumbani akamkuta mamake amenuna. Mama na mtoto wake mpotevu walitazamana kwa muda mrefu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15865_22', 3, NULL, 'assets/topic/asb/14992.png', 'Mtoto punda na mamake waliishi pamoja kwa ushirikiano.
