INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915', 2, 'Mtoto wa Kima (Colour -In)', 'assets/topic/asb/9057.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_3', 3, NULL, 'assets/topic/asb/9042.png', 'Katika msitu wa Miwa,  aliishi Kima aliyehuzunika. Kila alipomzaa mtoto, aliaga dunia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_4', 3, NULL, 'assets/topic/asb/9043.png', 'Uchungu wa kuwapoteza watoto ulimfanya Kima kuruka juu na chini. Angeruka kutoka tawi moja hadi nyingine huku akilia, "Kwi! Kwi! Kwi!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_5', 3, NULL, 'assets/topic/asb/9044.png', 'Kima alihuzunika zaidi alipowaona kima wengine wakiwa na watoto wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_6', 3, NULL, 'assets/topic/asb/9045.png', 'Kima mwenye huzuni alikuwa na tabia ya kuketi juu ya tawi na kutazama juu! Siku zilivyopita, ndivyo Kima alilia zaidi na zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_7', 3, NULL, 'assets/topic/asb/9046.png', 'Baadaye, alimzaa mtoto mwingine. Aliamua kumpeleka njiani ili watu wote waliopita wamwone na kumtakia heri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_8', 3, NULL, 'assets/topic/asb/9047.png', 'Siku moja, Kima alishuka kutoka mtini na kumweka mtoto wake njiani. Wakati huo, mwindaji mmoja alikuwa akirudi nyumbani. Alimwona mtoto wa Kima aliyelala kando ya njia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_9', 3, NULL, 'assets/topic/asb/9048.png', 'Mwindaji huyo alimbeba mtoto wa Kima na kumpeleka nyumbani kwake. Alipofika, kila mmoja wa wanawe watatu alitaka kumbeba mtoto wa Kima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_10', 3, NULL, 'assets/topic/asb/9054.png', 'Walicheza na kuimba:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_11', 3, NULL, 'assets/topic/asb/9058.png', 'Walipokuwa wakicheza na mtoto wa Kima, mama Kima alijificha mtini na kuwatazama. Alihofia kwamba mwanawe angekufa kama vile wale wengine walivyokufa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_12', 3, NULL, 'assets/topic/asb/9059.png', 'Mke wa mwindaji aliwaona wanawe wakicheza na yule mtoto wa Kima. "Tahadharini wanangu! Hebu mleteni hapa. Mtamwangusha!" Akasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_13', 3, NULL, 'assets/topic/asb/9057.png', 'Alimshika mtoto wa Kima na kumbariki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12915_14', 3, NULL, 'assets/topic/asb/9060.png', 'Kisha mke wa mwindaji alimweka mtoto wa Kima chini. Mama Kima akamchukuwa na kumbeba mtoto wake kifuani. Halafu akatoweka mtini. Hakumpoteza tena mtoto mwingine!', NULL);
