INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812', 2, 'Mtoto wa miujiza', 'assets/topic/asb/14779.png', 'Alice Edui
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_3', 3, NULL, 'assets/topic/asb/14777.png', 'Hapo kale aliishi mzee mmoja aliyeitwa Lotum.  Aliwafuga mbuzi, ngamia, ng''ombe na punda.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_4', 3, NULL, 'assets/topic/asb/14778.png', 'Waliishi kwa muda mrefu bila kujaliwa mtoto. Baadaye, Akiru alijifungua mtoto mrembo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_5', 3, NULL, 'assets/topic/asb/14779.png', 'Siku moja, Akiru alimwacha mtoto nyumbani pekee ili aende kuwakama ng''ombe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_6', 3, NULL, 'assets/topic/asb/14780.png', 'Kila alipoachwa nyumbani, mtoto angeamka na kunywa maziwa yote kisha aanze kulia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_7', 3, NULL, 'assets/topic/asb/14781.png', 'Kila wakati Akiru alikipata kibuyu cha maziwa kikiwa kitupu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_8', 3, NULL, 'assets/topic/asb/14782.png', 'Baada ya muda, Akiru alimjulisha mumewe Lotum mambo hayo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_9', 3, NULL, 'assets/topic/asb/14783.png', 'Lotum alijificha chumbani humo. Mara alimwona mtoto akibugia kibuyu cha maziwa mdomoni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_10', 3, NULL, 'assets/topic/asb/14784.png', 'Lotum na mkewe walipogundua haya, waliamua kuondoka usiku mtoto alipokuwa amelala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_11', 3, NULL, 'assets/topic/asb/14785.png', 'Walimwachia mtoto wao ng''ombe mmoja, ngamia mmoja na mbuzi mmoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_12', 3, NULL, 'assets/topic/asb/14786.png', 'Mtoto alipoamka alishangazwa na jinsi nyumba ilivyokuwa tulivu. Akawaona wanyama alioachiwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_13', 3, NULL, 'assets/topic/asb/14787.png', 'Siku moja ng''ombe akiwa malishoni, mwanamume mmoja alimwona, akafikiria, "Nitamchinja ng''ombe huyu nipate kitoweo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_14', 3, NULL, 'assets/topic/asb/14788.png', 'Mara akachomoa mkuki na kumfuma tumboni akafa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_15', 3, NULL, 'assets/topic/asb/14789.png', 'Mtoto huyo alitoka tumboni mwa ng''ombe na kujinatisha katika kichwa cha mwanamume huyo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_16', 3, NULL, 'assets/topic/asb/14790.png', 'Walipofika nyumbani, mtoto alijinasua na kukaa mwambani. Mwanamume alimchinjia mtoto kondoo dume aliyenona.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14812_17', 3, NULL, 'assets/topic/asb/14791.png', 'Usiku huo alitoweka asionekane tena.', NULL);
