INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_3', 3, NULL, 'assets/topic/asb/309.png', 'Jembe lake lilikuwa fupi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_4', 3, NULL, 'assets/topic/asb/310.png', 'Mlango wake ulikuwa chini sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_5', 3, NULL, 'assets/topic/asb/311.png', 'Kitanda chake kilikuwa kifupi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_6', 3, NULL, 'assets/topic/asb/314.png', 'Baisikeli lake lilikuwa fupi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_7', 3, NULL, 'assets/topic/asb/313.png', 'Mtu huyu alikuwa mrefu sana!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_8', 3, NULL, 'assets/topic/asb/316.png', 'Alitengeneza mti wa jembe mrefu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_9', 3, NULL, 'assets/topic/asb/315.png', 'Alitengeneza mlango mrefu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_10', 3, NULL, 'assets/topic/asb/317.png', 'Alitengeneza kitanda kirefu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_11', 3, NULL, 'assets/topic/asb/318.png', 'Alinunua baiskeli iliyo juu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_12', 3, NULL, 'assets/topic/asb/319.png', 'Alikalia kiti kirefu sana. Alikula na uma ndefu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24769_13', 3, NULL, 'assets/topic/asb/320.png', 'Alihama kutoka kwa nyumba yake na kuishi msituni. Aliishi miaka mingi.', NULL);
