INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689', 2, 'Mtu na Simba', 'assets/topic/asb/8180.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_3', 3, NULL, 'assets/topic/asb/8180.png', 'Hapo zamani za kale, palikuwa na mtu aliyekuwa akitembea kichakani. Ghafla, alimkuta Simba!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_4', 3, NULL, 'assets/topic/asb/8181.png', 'Ulikuwa msimu wa baridi. Simba alikuwa akipumzika kwenye nyasi fupi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_5', 3, NULL, 'assets/topic/asb/8182.png', 'Makutano hayo yalitokea ghafla mtu huyo asijue la kufanya. Alijawa na woga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_6', 3, NULL, 'assets/topic/asb/8184.png', 'Mwanzoni, Simba pia alishangaa na kuogopa kidogo. Lakini, mngurumo mkubwa ulisikika kutoka tumboni mwake kwani alikuwa na njaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_7', 3, NULL, 'assets/topic/asb/8188.png', 'Mtu yule alijikuta juu ya mti mrefu. Alianza kupiga mayowe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_8', 3, NULL, 'assets/topic/asb/8189.png', 'Simba alilala chini ya mti akisubiri mlo. Muda ulipopita, Simba alijifanya kuwa analala usingizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_9', 3, NULL, 'assets/topic/asb/8190.png', 'Muda mfupi baadaye, mtu yule alilala usingizi. Kisha akaamka tena. Mwishoni alisinzia na kulala fofofo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_10', 3, NULL, 'assets/topic/asb/8191.png', 'Aliporomoka kutoka juu na kumwangukia yule Simba aliyelala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_11', 3, NULL, 'assets/topic/asb/8192.png', 'Wote waliamka wakiwa wamechanganyikiwa na kuogopa. Simba alisahau mlo wake na kukimbilia usalama wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_12', 3, NULL, 'assets/topic/asb/8193.png', 'Mtu yule alianza kukimbia akielekea upande mmoja na Simba. Aligundua kwamba alikuwa akikimbia na Simba!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12689_13', 3, NULL, 'assets/topic/asb/8194.png', 'Kwa hivyo alibadilisha mwelekeo wake na kuepuka kuliwa na Simba!', NULL);
