INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340', 2, 'Mulongo na Fisi', 'assets/topic/asb/1419.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_3', 3, NULL, 'assets/topic/asb/1420.png', 'Palikuwa na msichana aliyeitwa Mulongo. Aliishi na wazazi wake karibu na kijiji cha Budongo. Siku moja mamake Mulongo alimtuma kuchota maji kisimani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_4', 3, NULL, 'assets/topic/asb/1421.png', 'Njiani, alikutana na marafiki zake wakienda msituni kutafuta kuni. Mulongo alitaka kwenda nao. "Tafadhali, nisubiri chini ya mti huu!" alisema, "Lazima nimchotee mama maji." Lakini marafiki zake hawakutaka kusubiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_5', 3, NULL, 'assets/topic/asb/1422.png', '"Sawa, nitawakuta huko msituni!" Mulongo alisema huku akikimbia kwenda kumchotea mamake maji. Kisha akaenda msituni kuwatafuta marafiki zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_6', 3, NULL, 'assets/topic/asb/1423.png', 'Alifuata njia iliyoelekea kwenye kijito. Upande mwingine wa kijito kulikuwa na njia nyingine ndogo kila mojawapo ikielekea upande tofauti. "Njia ipi marafiki zake walifuata?" Alishangaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_7', 3, NULL, 'assets/topic/asb/1424.png', 'Aliichagua njia iliyokuwa pana kisha akatembea, na kutembea, lakini hakuwapata marafiki zake. Alichoka sana kwa hivyo alipopumzika chini ya mti na kulala muda mfupi baadaye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_8', 3, NULL, 'assets/topic/asb/1425.png', 'Mulongo alipoamka, ilikuwa giza. Gizani, macho ya manjano yalimulika. Alikuwa amezungukwa na fisi! Aliogopa asiweze hata kulia. Alijaribu kukimbia,  lakini fisi walimzunguka wakionyesha wazi kuwa walikuwa na njaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_9', 3, NULL, 'assets/topic/asb/1426.png', '"Usisonge," sauti ya fisi mkubwa zaidi ilisikika. "Ukikimbia, utaliwa!" "Tafadhali, niache niende nyumbani!" Mulongo aliwasihi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_10', 3, NULL, 'assets/topic/asb/1427.png', 'Badala yake fisi walimpeleka Mulongo katika nyumba yao iliyokuwa msituni. Chumba kilikuwa kichafu kilichojaa mifupa na kelele za nzi. Alilala chini na kujifanya kuwa amepatwa usingizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_11', 3, NULL, 'assets/topic/asb/1428.png', 'Gizani, aliwasikia fisi wakiongea wenyewe kwa wenyewe. "Moto unaendelea vipi? Je, maji yanachemka?" Mmoja aliuliza." Jibu lilikuwa, "Kila kitu ni tayari. Nimlete sasa?" "Ndiyo, ndiyo!" Fisi wale wengine walinguruma. "Tunahisi njaa!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_12', 3, NULL, 'assets/topic/asb/1419.png', 'Walikuwa karibu kumvuta Mulongo kumtoa chumbani wakati fisi mkubwa aliongea, "Fisi,  subirini. Kumbuka kanuni ya kijiji. Hakuna fisi anayekubaliwa kula peke yake. Ni lazima tuialike jamii nzima kushiriki mlo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_13', 3, NULL, 'assets/topic/asb/1429.png', 'Hwakurudi haraka. Yule fisi mkubwa alianza kusinzia na kukoroma pale mbele ya moto. Hii ilikuwa nafasi nzuri ya Mulongo kutoroka! Lakini angempitaje yule fisi?
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_14', 3, NULL, 'assets/topic/asb/1430.png', 'Njia ilikuwa ni moja tu. Alijihimiza na kurukia juu ya mgongo wa fisi kwa mara moja, kisha akakimbia, na kukimbia jinsi miguu yake alivyomwezesha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_15', 3, NULL, 'assets/topic/asb/1431.png', 'Wakati huo huo, fisi wale wengine walirudi na kuona kilichotokea. Walimfuata huku wakipiga kelele za hasira na gadhabu. Lakini, walikuwa wamechelewa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_16', 3, NULL, 'assets/topic/asb/1432.png', 'Alipokaribia kijiji chake, wanakijiji walimtambua na kushangilia wakisema, "Mulongo, Mulongo anakuja, Mulongo anakuja." Wazazi wake walikimbia kumbusu huku wakishukuru Mungu kwa kumwokoa mototo wao, "Mulongo, tulidhani umekufa!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2340_17', 3, NULL, 'assets/topic/asb/2135.png', 'Kutoka siku hiyo, Mulongo na watoto wengine hawakuwahi kururdi msituni pekee yao.', NULL);
