INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352', 2, 'Musa na paka', 'assets/topic/asb/26349.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_3', 3, NULL, 'assets/topic/asb/26346.png', 'Musa anaishi na wazazi wake katika kijiji cha Mafunzo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_4', 3, NULL, 'assets/topic/asb/26347.png', 'Musa pia ni mchokozi. Yeye huwapiga wenzake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_5', 3, NULL, 'assets/topic/asb/26348.png', 'Siku moja anapokuwa akitoka shuleni, anamwona paka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_6', 3, NULL, 'assets/topic/asb/26349.png', 'Babake anapomwona, anamwambia, "Musa, acha kumnyanyasa paka huyo! Mrudishe mahali ulikompata."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_7', 3, NULL, 'assets/topic/asb/26350.png', 'Musa anakataa kumrudisha paka alikomuokota. Badala yake, anamfungia katika ngome.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_8', 3, NULL, 'assets/topic/asb/26351.png', 'Musa anapolala usiku huo, anaota ndoto.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_9', 3, NULL, 'assets/topic/asb/26352.png', 'Paka anaanza kumeza miguu ya Musa. Anaikula hadi kwenye magoti.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22352_10', 3, NULL, 'assets/topic/asb/26353.png', 'Siku inayofuata, Musa anamrejesha paka mahali alikomuokota.
