INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600', 2, 'Mvulana aliyechukiwa na wote', 'assets/topic/asb/7991.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_3', 3, NULL, 'assets/topic/asb/9191.png', 'Hapo zamani za kale, kulikuwa na mvulana mwenye sura mbaya sana. Hakupendwa na yeyote, hata wazazi wake. Hata nyumbani, aliketi peke yake wengine walipotazama runinga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_4', 3, NULL, 'assets/topic/asb/7987.png', 'Shuleni pia aliketi peke yake watoto wengine walipokuwa wakicheza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_5', 3, NULL, 'assets/topic/asb/7988.png', 'Walipokwenda kuwaleta mifugo, yeye alitembea nyuma yao. Watoto wengine hawakutaka kutembea pamoja naye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_6', 3, NULL, 'assets/topic/asb/7989.png', 'Alijua kuwa hakuna aliyemtaka wala kuzungumza naye. Siku moja aliamua kutoroka, lakini hakujua pa kwenda. Alitembea hadi kwenye msitu na kuanza kuogopa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_7', 3, NULL, 'assets/topic/asb/7990.png', 'Alipokaribia msituni, alikutana na bibi mkongwe. Alikuwa mchafu na mwenye sura mbaya. Bi Kizee alimsalimia na kumwuliza, "Unafanya nini katika msitu huu wa kuogofya?" Mvulana alishangaa kuzungumziwa na Bi Kizee. "Ninakwenda popote. Hakuna yeyote anipendaye wala kunizungumzia," alimwambia Bi Kizee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_8', 3, NULL, 'assets/topic/asb/7991.png', 'Bi Kizee alimwuliza ikiwa angehitaji usaidizi. "Ndiyo," alijibu haraka. Bi Kizee alimwambia, "Kabla sijakusaidia, nataka ulambe uso wangu hadi uwe wa kupendeza."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_9', 3, NULL, 'assets/topic/asb/7992.png', 'Mvulana alikubali kuulamba uso wa Bi Kizee yule ili asaidiwe. Aliulamba uso wa Bi Kizee kuanzia machoni, akamfuta kamasi puani na kumtoa nta masikioni. Aliulamba uso wa Bi Kizee hadi ukapendeza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_10', 3, NULL, 'assets/topic/asb/7993.png', 'Uso wa Bi Kizee ulimetameta na akamshukuru. Alisema, "Humu msituni utapata vitu vingi vya ajabu,  lakini usichukuwe chochote ila tu mzizi utakaoupata."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_11', 3, NULL, 'assets/topic/asb/7994.png', 'Mvulana alitamani sana kuviona vitu vyote vya ajabu. Kabla hajaingia msituni, Bi Kizee alimwita, "Hebu,  rudi hapa!" "Niko hapa!" Mvulana alisema. Bi Kizee alimuuliza, "Ulisikia nilichosema? Msituni, utapata vitu vingi vya ajabu, lakini usichukuwe chochote ila tu mzizi utakaoupata." Mvulana aliitikia na akakubaliwa kuingia msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_12', 3, NULL, 'assets/topic/asb/7995.png', 'Alipoanza kukimbia kwenda msituni, alisikia sauti, "Wewe, rudi hapa!" Mvulana hakujua alichotaka yule Bi Kizee. Akarudi. Bi Kizee alimwambia, "Sina hakika kama umenielewa. Nimesema, utapata vitu vingi vya ajabau msituni, lakini usichukuwe chochote ila mzizi utakaoupata." Mvulana aliitikia na kisha akakimbilia msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_13', 3, NULL, 'assets/topic/asb/7996.png', 'Mara tu alipoingia msituni, aliisikia sauti ya Bi Kizee tena, "Rudi hapa!" Mvulana alijaribu kuipuuza, lakini sauti ikaendelea kumwita. Bi Kizee akasema, "Sina uhakika utatenda ninavyokwambia. Utapata vitu vingi vya ajabu msituni, lakini usichukuwe chochote ila tu mzizi utakaoupata." Mvulana alianza kuudhika na marudio haya. Alikimbia hadi msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_14', 3, NULL, 'assets/topic/asb/7997.png', 'Mle msituni, aliliona bakuli lililojaa fedha tele. Alizichukuwa na kuziweka mfukoni. Mara akakumbuka alichoambiwa na Bi Kizee. Pole pole,  alizitoa fedha mfukoni na kuzirejesha kwenye bakuli.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_15', 3, NULL, 'assets/topic/asb/7998.png', 'Kisha akatazama karibu naye akaona nguo mpya nzuri. Akavua nguo alizokuwa nazo na kuvaa zile mpya. Halafu akakumbuka aliyoambiwa na Bi Kizee. Haraka, akazivua zile nguo mpya na kuvaa alizokuwa nazo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_16', 3, NULL, 'assets/topic/asb/7999.png', 'Alipotazama hapo kando, aliuona mzizi! Ulikuwa umenyauka kutokana na jua. Akajiuliza, "Bi Kizee atafaidikaje na mzizi huu?" Juu ya mzizi, kulikuwa na bakuli la chakula kilichonukia. Mvulana akahisi njaa. Hakuweza kujizuia kukila.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_17', 3, NULL, 'assets/topic/asb/8000.png', 'Ghafla, akakumbuka alichoambiwa na yule Bi Kizee! Alikumbuka aliambiwa auchukuwe mzizi tu. Alianza kuutafuta mzizi, lakini hakuupata.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12600_18', 3, NULL, 'assets/topic/asb/8001.png', 'Akiwa ameudhika, alirudi kumwambia yule Bi Kizee kilichotokea. Bi Kizee tayari alikuwa ameenda zake.', NULL);
