INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609', 2, 'Mwalimu wangu', 'assets/topic/asb/8513.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_3', 3, NULL, 'assets/topic/asb/8509.png', 'Ninampenda mwalimu wangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_4', 3, NULL, 'assets/topic/asb/8510.png', 'Ni mwalimu mzuri, mvumilivu na mwenye huruma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_5', 3, NULL, 'assets/topic/asb/8511.png', 'Amesuka nywele zake vizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_6', 3, NULL, 'assets/topic/asb/8512.png', 'Ameweka shanga maridadi katika nywele zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_7', 3, NULL, 'assets/topic/asb/8513.png', 'Shanga zake ni za kijani, nyeupe, nyekundu, na manjano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_8', 3, NULL, 'assets/topic/asb/8514.png', 'Tafadhali mama, nisuke nywele zangu kama vile za mwalimu wangu?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_9', 3, NULL, 'assets/topic/asb/8515.png', 'Nitakapokuwa mtu mzima, nitaweka shanga katika nywele zangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15609_10', 3, NULL, 'assets/topic/asb/8516.png', 'Nikikua, nitakuwa kama vile mwalimu wangu alivyo!', NULL);
