INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166', 2, 'Mwana wa Tumbili (Colour-in)', 'assets/topic/asb/9057.png', 'Baraka Fatma
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_3', 3, NULL, 'assets/topic/asb/9042.png', 'Kwenye msitu wa Miwa paliishi tumbili aliyekuwa na huzuni sana. Kila mara alipozaa, mtoto wake alifariki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_4', 3, NULL, 'assets/topic/asb/9043.png', 'Uchungu wa kufiliwa na watoto wake ulimfanya tumbili kuruka juu na chini. Tumbili huyu alikuwa akirukia tawi hili hadi kwenye lingine, akipiga mayowe, Kwi! Kwi! Kwi! Kwi!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_5', 3, NULL, 'assets/topic/asb/9044.png', 'Tumbili alikuwa na huzuni wakati wote. Huzuni wake ulimzidi haswa alipowaona akina mama tumbili wengine na watoto wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_6', 3, NULL, 'assets/topic/asb/9045.png', 'Tumbili mwenye huzuni alijiwayawaya huku na huku. Alijiwayawaya kwenye tawi hili na lile! Siku zilipita na tumbili alizidi kulia zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_7', 3, NULL, 'assets/topic/asb/9046.png', 'Halafu, alizaa tena. Aliamua kumpeleka mtoto wake njiani ili wapita njia wamwone na kumtakia heri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_8', 3, NULL, 'assets/topic/asb/9047.png', 'Tumbili alishuka na kumweka mtotowe njiani. Wakati huo, mwindaji alikuwa akirejea nyumbani. Alimwona mtoto wa tumbili akilala kando ya njia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_9', 3, NULL, 'assets/topic/asb/9048.png', 'Mwindaji alimbeba mwana tumbili hadi nyumbani kwake. Alipofika nyumbani, wavulana wake watatu walifurahi kumwona mwana tumbili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_10', 3, NULL, 'assets/topic/asb/9054.png', 'Watatu hao waliimba: Mrushe juu! Mrushe chini! Mrushe kwangu! Mrushe kwake! Mwana tumbili alirushwa kutoka kwa mmoja hadi mwingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_11', 3, NULL, 'assets/topic/asb/9058.png', 'Wavulana wale walipoendelea kucheza na mwana tumbili, tumbili alijificha mtini na kuangalia. Aliogopa kuwa mtoto huyu naye angefariki kama wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_12', 3, NULL, 'assets/topic/asb/9059.png', 'Mara mke wa mwindaji aliwaona watoto wakicheza na tumbili mtoto. Aliwaonya, "Kuwa waangalifu! Hebu mlete hapa. Mtamwangusha!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_13', 3, NULL, 'assets/topic/asb/9057.png', 'Alimchukuwa mwana tumbili mikononi mwake na kumbariki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10166_14', 3, NULL, 'assets/topic/asb/9060.png', 'Alipomweka mwana tumbili chini, tumbili alikimbia kwa furaha. Alimbeba mwanawe kifuani na kwenda mtini.', NULL);
