INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068', 2, 'Mwanamke mlafi', 'assets/topic/asb/19126.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_3', 3, NULL, 'assets/topic/asb/19113.png', 'Hapo kale, kulikuwa na mtu aliyeitwa Pepe. Aliishi katika mtaa wa Umoja.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_4', 3, NULL, 'assets/topic/asb/19114.png', 'Siku moja Pepe alikuwa mgonjwa. Hakuwa na yeyote wa kumsaidia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_5', 3, NULL, 'assets/topic/asb/19115.png', 'Pepe aliwaalika marafiki na jamaa zake kuhudhuria harusi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_6', 3, NULL, 'assets/topic/asb/19116.png', 'Bi harusi alipatiwa vikapu vya wimbi, mikeka, ndizi, njugu, na zawadi nyingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_7', 3, NULL, 'assets/topic/asb/19117.png', 'Baada ya sherehe ya harusi, wageni waliondoka kurudi makwao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_8', 3, NULL, 'assets/topic/asb/19118.png', 'Siku iliyofuata, Pepe alimwandalia mkewe ndizi tamu, lakini alikataa kuzila.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_9', 3, NULL, 'assets/topic/asb/19119.png', 'Lakini Pepe alipokwenda kuwinda, mwanamke huyo alizila ndizi zote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_10', 3, NULL, 'assets/topic/asb/19120.png', 'Baadaye Pepe aliporudi kutoka mawindoni alihisi njaa. Alitaka kuzila zile ndizi tamu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_11', 3, NULL, 'assets/topic/asb/19121.png', 'Siku iliyofuata, Pepe alikwenda shambani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_12', 3, NULL, 'assets/topic/asb/19123.png', 'Siku nyingine, Pepe alikwenda kumtembelea rafikiye.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_13', 3, NULL, 'assets/topic/asb/19124.png', 'Pepe aligundua kuwa mwanamke huyo alikuwa na  shida. Aliamua kumnasa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_14', 3, NULL, 'assets/topic/asb/19125.png', 'Mwanamke alikiona chungu kilichokuwa kimejaa maziwa. Alikichukua akakiweka mdomoni. Aliyanywa maziwa yote huku mbwa akimtazama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_15', 3, NULL, 'assets/topic/asb/19126.png', 'Kwa bahati mbaya, chungu kilikwama mdomoni. Alijaribu kukiondoa, lakini ilikuwa bure. Alipiga kelele na kuruka juu na chini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_16', 3, NULL, 'assets/topic/asb/19127.png', 'Mbwa alikimbia kumtafuta Pepe. Alibweka, akabweka, akaruka juu na chini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22068_17', 3, NULL, 'assets/topic/asb/19128.png', 'Walikimbia pamoja kwenda nyumbani. Pepe alistaajabu kumpata mkewe na chungu mdomoni.
