INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_4', 3, NULL, 'assets/topic/asb/310.png', 'Mlango wake ulikuwa chini mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_5', 3, NULL, 'assets/topic/asb/311.png', 'Kitanda chake kilikuwa kifupi mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_6', 3, NULL, 'assets/topic/asb/314.png', 'Baisikeli yake ilikuwa fupi mno.', NULL);
