INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_10', 3, NULL, 'assets/topic/asb/317.png', 'Akakitengeneza kitanda kirefu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_11', 3, NULL, 'assets/topic/asb/318.png', 'Kisha akainunua baiskikeli iliyokuwa refu mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1176_12', 3, NULL, 'assets/topic/asb/319.png', 'Akaketi juu ya kiti kilichokuwa juu sana. Akala kwa kutumia uma iliyokuwa refu mno.', NULL);
