INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600', 2, 'Mwanamume aliyekuwa na tatizo kubwa', 'assets/topic/asb/19570.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_3', 3, NULL, 'assets/topic/asb/19567.png', 'Munia aliishi na babu, bibi, shangazi na mjomba. Aliishi pia na  kuku wake, mbuzi, kondoo na nguruwe. Wote walikaa kwenye chumba kimoja. Kuku alikuwa na kitundu chake. Mbuzi, kondoo na nguruwe walikuwa wanafungwa pembeni karibu na ukuta. Bibi na babu walikuwa bado na afya nzuri nao shangazi na mjomba walisaidia kufanya kazi za pale nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_4', 3, NULL, 'assets/topic/asb/19568.png', 'Hata hivyo, mambo yalibadilika miaka michache baadaye. Bibi na babu wa Munia walizeeka na hawakuweza kusikia wala kuona vizuri. Shangaziye aliugua na hakuweza kusaidia katika usafi wa nyumba. Mjombake alilala wakati wote na hakusaidia katika shughuli za kuwalisha wanyama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_5', 3, NULL, 'assets/topic/asb/19569.png', 'Wanyama walipiga kelele nyingi kwa sababu walikuwa na njaa. Nyumba pia ilijaa uchafu kwa sababu shangazi hakuweza kuisafisha. Munia alikuwa mchovu wakati wote kwa sababu alitoka kila siku kuwatafutia wote chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_6', 3, NULL, 'assets/topic/asb/19570.png', 'Munia hakuweza kukabilina na hali hiyo tena. Aliwandea babu na bibi kuwauliza suluhisho. Lakini,  alihuzunika sana kwa sababu aliondoka bila suluhisho kwani hawakuweza kumsikia. Shangaziye mgonjwa alisema, "Ninachohitaji ni kuweza kulala usiku na kuamka asubuhi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_7', 3, NULL, 'assets/topic/asb/19571.png', 'Mjombake mzembe alisema, "Wauze wanyama hawa kisha uwape babu na bibi kileo. Watalala wakome kukusumbua." Munia alisema kwa hasira "Mjomba, unajua kwamba siwezi kuwauza wanyama hawa. Wao pekee ndio mali yangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_8', 3, NULL, 'assets/topic/asb/19572.png', 'Usiku mmoja, Munia hakupata usingizi. Alimkumbuka mtu mmoja aliyekuwa na busara. Mtu huyo aliaminika kuyajibu maswali yote na kusuluhisha matatizo yote.  Munia aliwachukuwa wanyama wake akaenda nao kumtembelea yule mtu. Aliwafunga mbuzi, kondoo na nguruwe kwa Kamba na akambeba kuku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_9', 3, NULL, 'assets/topic/asb/19573.png', 'Munia alifika nyumbani kwa mtu huyo akiwa amechoka na mwenye njaa sana. Baada ya kueleza shida yake, mtu huyo alisema, "Nitakusaidia, lakini ni sharti ufanye nikuambiavyo." Munia alijibu,  "Nitafanya chochote kusuluhisha tatizo langu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_10', 3, NULL, 'assets/topic/asb/19574.png', 'Mtu yule alimwambia, "Waache wanyama hapa kisha urudi nyumbani." Munia alisema, "Nimeishi na wanyama hawa wangu kwa muda mrefu. Wao ndio mali pekee niliyo nayo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_11', 3, NULL, 'assets/topic/asb/19575.png', 'Mtu akajibu Munia, "Nilikuambia nitakusaidia lakini ni lazima ufanye nikuambiavyo." Kwa vile Munia alihitaji suluhisho kwa tatizo lake, aliwaacha wanyama akarudi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_12', 3, NULL, 'assets/topic/asb/19576.png', 'Nyumba yake ilikuwa na kimya kingi na ilikuwa tupu. Babu na bibi walilalamika kwamba hawakupata maziwa kutoka kwa mbuzi. Mjombake alisema, "Tumekuwa maskini sana, kila mtu anatucheka."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_13', 3, NULL, 'assets/topic/asb/19577.png', 'Munia hakuweza tena kuvumilia malalamishi yao. Pia, aliwakosa wanyama wake. Alirudi kwa mtu yule mwenye busara ili kumwuliza mawaidha. "Maisha yangu yamekuwa fotauti bila wanyama wangu. Jamaa zangu wanalalamika sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_14', 3, NULL, 'assets/topic/asb/19578.png', 'Mtu yule alimwambia, "Nitakusaidia, lakini, ni lazima ufanye nikuambiavyo. Nenda nyumbani kisha uwatoe nje bibi na babu kutoka kwenye nyumba." Munia alimjibu, "Nitawezaje kufanya hivyo? Wao ni wavyele wangu na wananitegemea."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_15', 3, NULL, 'assets/topic/asb/19579.png', 'Mwishowe, Munia alienda nyumbani akafanya alivyoambiwa. Hakuweza kupata amani akilini mwake. Babu na bibi hawakuwa na mahali pa kuenda. Walirandaranda wakiangukia miti na kuhisi baridi usiku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_16', 3, NULL, 'assets/topic/asb/19580.png', 'Shangaziye mgonjwa alilia akisema, "Tafadhali usinitupe nje." Mjombake mzembe alisema,  "Sitaondoka kitandani usije ukanitupa nje pia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_17', 3, NULL, 'assets/topic/asb/19581.png', 'Munia hakuweza kustahimili mambo haya hata kidogo. Alirudi kwa mtu mwenye busara akiwa amechanganyikiwa na mwenye hasira. Alimlilia akisema, "Mambo haya ni magumu sana kwa mtu mmoja. Nipe suluhisho bora zaidi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19600_18', 3, NULL, 'assets/topic/asb/19582.png', 'Mtu mwenye busara alisema, "Hapa, wachukue wanyama wako na uwarudishe bibi na babu yako kwenye nyumba pia." Munia alifanya alivyoambiwa. Shangaziye alisema, "Nitafanya bidii kuisafisha nyumba." Mjombake aliacha kuwa mzembe akamsaidia Munia na kazi za pale nyumbani.
