INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400', 2, 'Mwanamume aliyeota manyoya', 'assets/topic/asb/18142.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_3', 3, NULL, 'assets/topic/asb/17994.png', 'Kulikuwa na tajiri aliyekuwa na watoto wa kiume wawili. Kwenye wosia wake alitaka mwanawe wa kwanza arithi ng''ombe wake wote na wa pili, amrithi jogoo mmoja tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_4', 3, NULL, 'assets/topic/asb/17995.png', 'Baada ya tajiri kufa, mwanawe wa kwanza alirithi ng''ombe wote. Mwanawe wa pili alipewa jogoo mmoja tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_5', 3, NULL, 'assets/topic/asb/17997.png', 'Baada ya muda mchache, mwana wa kwanza ambaye alirithi mali yote, aliugua. Alienda kumwona mganga amsaidie kupata njia ya kupona. Mganga alisema, "Ni sharti umchinje jogoo mwenye rangi ya pekee."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_6', 3, NULL, 'assets/topic/asb/18138.png', 'Mwana huyo hakujua atakapopata jogoo mwenye rangi ya pekee hadi alipokumbuka kuwa nduguye alikuwa na mmoja. Aliwatuma watu kumchukua yule jogoo wa nduguye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_7', 3, NULL, 'assets/topic/asb/18139.png', 'Kakake aliwaambia watu hao, "Mimi sina mali nyingine isipokuwa jogoo huyu tu. Lakini, iwapo jogoo huyu ataweza kumponya kakangu, basi ni bora kwangu kumpoteza jogoo huyu." Aliwapa jogoo wake kwa ukarimu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_8', 3, NULL, 'assets/topic/asb/18140.png', 'Watu walimchinja jogoo na kumlisha kakake mgonjwa. Baadaye, kakake alipona.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_9', 3, NULL, 'assets/topic/asb/18141.png', 'Baada ya muda mfupi, kitu kisicho cha kawaida kilifanyika. Mwili wake ulianza kuota manyoya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_10', 3, NULL, 'assets/topic/asb/18142.png', 'Hakuweza kuamini macho yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_11', 3, NULL, 'assets/topic/asb/18143.png', 'Alipokwenda kuwaona wazee, walisema, "Jambo hili limetokea kwa sababu ya laana. Hukumfanyia nduguyo haki. Umechukua ng''ombe wote na hata jogoo ambaye babako alimuachia. Ukitaka kupona, ni lazima nduguyo akusamehe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_12', 3, NULL, 'assets/topic/asb/18144.png', 'Wazee waliendelea kusema, "Ni sharti tuichukue bangili hii tumpe nduguyo kama ishara ya msamaha. Iwapo ataitemea mate bangili hii, utapona."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_13', 3, NULL, 'assets/topic/asb/18145.png', 'Wazee walimpelekea ndugu mdogo bangili hiyo wakamwambia, "Itemee mate bangili hii umsamehe kaka yako." Nduguye maskini aliitemea mate bangili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19400_14', 3, NULL, 'assets/topic/asb/18146.png', 'Kwa sababu hiyo, wazee waliamua kumpa nusu ya ng''ombe wa kakake tajiri.', NULL);
