INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061', 2, 'Mzozo wa Fisi na Kima', 'assets/topic/asb/18699.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_3', 3, NULL, 'assets/topic/asb/18696.png', 'Hapo zamani za kale, kulikuwa na Fisi na Kima. Walikuwa na mzozo kati yao. Walienda kwa hakimu ili awasaidie kuusuluhisha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_4', 3, NULL, 'assets/topic/asb/18697.png', 'Baada ya hakimu kusikiliza kesi yao, aliogopa kuamua. Aliwaza, "Nikimlaumu Fisi, atawala mifugo wangu. Nikimlaumu Kima, atayala mahindi yangu yote! Nifanyeje?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_5', 3, NULL, 'assets/topic/asb/18698.png', 'Hakimu alitafakari juu ya kesi hiyo kisha akasema, "Naona vigumu kuiamua kesi hii peke yangu. Ipelekeni kwa wazee wa kijiji."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_6', 3, NULL, 'assets/topic/asb/18699.png', 'Waliwaeleza wazee wa kijiji kuhusu mzozo wao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_7', 3, NULL, 'assets/topic/asb/18708.png', 'Wazee wa kijiji walijua wakimuunga Kima mkono, Fisi atawala mifugo wao. Na wakimuunga Fisi mkono, Kima atayala mahindi yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_8', 3, NULL, 'assets/topic/asb/18701.png', 'Wazee walimkumbuka mwanamke mmoja fukara aliyeishi hapo kijijini. Yeye hangepoteza chochote kwa kuwa hakuwa na mifugo wala mahindi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_9', 3, NULL, 'assets/topic/asb/18702.png', 'Walipowasili kwa mwanamke yule fukara walisema, "Tuamulie mzozo wetu." Mwanamke fukara alikubali lakini, akataka kuongea na kila mmoja peke yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_10', 3, NULL, 'assets/topic/asb/18703.png', 'Mwanamke fukara alimwita Fisi kwanza akamwambia, "Wewe unaheshimika, ni mnyama mkubwa tena ni shujaa. Unawezaje kugombana na mnyama mdogo tena mjinga kama Kima?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_11', 3, NULL, 'assets/topic/asb/18704.png', 'Mwanamke akaendelea kumwambia Fisi, "Watu wakisikia mzozo huu, watakudharau sana. Wacha kuzozana na Kima."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_12', 3, NULL, 'assets/topic/asb/18705.png', 'Mwanamke fukara akamwita Kima akamwambia, "Wewe ni mwerevu na mrembo. Mbona unabishana na mnyama huyu mchafu, mwenye harufu mbaya anayekula vilivyooza. Watu watakufikiriaje wakisikia unazozana na huyu kiumbe mwenye sura mbaya?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_13', 3, NULL, 'assets/topic/asb/18706.png', 'Baadaye, mwanamke fukara aliwaita wote akasema, "Kwa kuwa nyote mlikubali kuacha ugomvi kati yenu, ni lazima msameheane."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22061_14', 3, NULL, 'assets/topic/asb/18707.png', 'Kima na Fisi walisameheana wakasuluhisha mzozo wao.
