INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863', 2, 'Namorutunga', 'assets/topic/asb/11754.png', 'Translators without Borders and Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_3', 3, NULL, 'assets/topic/asb/11743.png', 'Zamani za kale, katika kijiji kimoja cha Turkana,  wanakijiji walikutana kila jioni kucheza ngoma ya kienyeji iitwayo edonga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_4', 3, NULL, 'assets/topic/asb/11747.png', 'Wachezaji wa kijiji hicho walifahamika kila pahali. Walikuwa na ustadi mkubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_5', 3, NULL, 'assets/topic/asb/11748.png', 'Watu kutoka vijiji jirani walifika kijijini humo kucheza ngoma ya edonga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_6', 3, NULL, 'assets/topic/asb/11749.png', 'Siku moja mgeni aliyeusikia umaarufu wa ngoma hiyo ya edonga, alituma mjumbe wake aende kuipeleleza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_7', 3, NULL, 'assets/topic/asb/11750.png', 'Mjumbe alipofika, wanakijiji walijawa na hofu. Wakajiuliza, "Huyo mgeni ni nani? Mbona amemtuma mjumbe wake?" Wanakijiji walijitayarisha kama kawaida. Walimchinja mbuzi na kupika chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_8', 3, NULL, 'assets/topic/asb/11751.png', 'Vilevile walikutana wakaandaa kafara na kusubiri siku nzima hadi jioni ila mgeni hakufika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_9', 3, NULL, 'assets/topic/asb/11752.png', 'Walicheza ngoma kama kawaida hadi usiku wa manane. Usiku huo kulikuwa na wachezaji ngoma wengi na kila mtu alikuwa amesisimka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_10', 3, NULL, 'assets/topic/asb/11753.png', 'Kabla ya ngoma kuisha, mgeni alifika. Kwa mavazi alifanana na wanakijiji na hawakumtambua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_11', 3, NULL, 'assets/topic/asb/11754.png', 'Alipopata nafasi ya kucheza ngoma, alijiunga na wanaume wengine. Ilionekana wazi kuwa alicheza tofauti na wenyeji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_12', 3, NULL, 'assets/topic/asb/11755.png', 'Watu waliaanza kumcheka. Wachezaji wengine walicheka wakiwa wamepiga magoti. Wengine walikaa kitako. Wengine walianguka chini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_13', 3, NULL, 'assets/topic/asb/11756.png', 'Aliacha kucheza ngoma ghafla. Mara hiyo hiyo, kila mchezaji akabadilika kuwa jiwe. Waliokuwa wamelala, waliokuwa wamesimama, waliokaa kitako na wengine waliopiga magoti wote walibaki walivyokuwa. Kisha mgeni akatoweka kijijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15863_14', 3, NULL, NULL, 'Mawe hayo yamebaki mahali pale hadi wa leo. Saa za usiku husikika yakiimba na kucheza edonga. Hiyo ndiyo asili ya Namorutunga.', NULL);
