INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874', 2, 'Namukhaywa', 'assets/topic/asb/5807.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_3', 3, NULL, 'assets/topic/asb/5806.png', 'Hapo zamani katika kijiji cha Lunyiko, kuliishi mwanamme aliyeitwa Ndong''a na mkewe Namukhaywa. Walikuwa na binti sita.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_4', 3, NULL, 'assets/topic/asb/5807.png', 'Namukhaywa alipokuwa na mimba ya saba,  Ndong''a alimwonya, "Ukimzaa msichana, nitakuua. Lakini ukinipa mtoto mvulana, nitakuandalia karamu kubwa!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_5', 3, NULL, 'assets/topic/asb/5808.png', 'Wakati wa kujifungua ulipowadia, Namukhaywa alienda kwa mkunga wa kienyeji. Je, mtoto atakuwa msichana au mvulana?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_6', 3, NULL, 'assets/topic/asb/5809.png', 'Alikuwa mvulana na msichana, mapacha! Mvulana alimwita Mukhwana, na msichana akamwita Mulongo. Alipomwangalia Mukhwana, alifurahi sana. Lakini alipomwangalia Mulongo, alihuzunika mno. Ni jambo moja tu angelifanya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_7', 3, NULL, 'assets/topic/asb/5810.png', 'Namukhaywa alimwacha Mulongo chini ya ulezi wa mkunga na kumpeleka Mukhwana nyumbani kwa mumewe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_8', 3, NULL, 'assets/topic/asb/5812.png', 'Kwa fahari, alimwonyesha Ndong''a mtoto mvulana. Alifurahi sana na kuwaita wanakijiji kusherehekea kuzaliwa kwa mtoto wake mvulana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_9', 3, NULL, 'assets/topic/asb/5813.png', 'Mukhwana alikua na kuwa kijana mwanamme wa kupendeza. Na Mulongo naye, akawa mwanamke mrembo mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_10', 3, NULL, 'assets/topic/asb/5814.png', 'Siku moja wakati alikuwa akichunga mifugo wa babake, Mukhwana alimwona msichana mrembo sana. "Huyu ndiye msichana ningependa kuoa," alijiambia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_11', 3, NULL, 'assets/topic/asb/5815.png', 'Lakini alipomwuliza kumuoa, aliimba hivi:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_12', 3, NULL, 'assets/topic/asb/5817.png', 'Hii ilifanyika tena na tena. Mukhwana hakujua la kufanya. Alimwendea mamake, Namukhaywa, akasema, "Nimempata msichana mrembo ambaye ningependa kumuoa. Lakini kila ninapomwuliza,  anaimba wimbo ule ule."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_13', 3, NULL, 'assets/topic/asb/5818.png', 'La kushangaza, Namukhaywa alisema,  "Anachoimba msichana yule ni kweli. Yeye ni dadako. Mlizaliwa mapacha. Babako alimtaka mtoto mvulana na wala si msichana. Nilimwacha dadako kwa mkunga nikakuleta wewe nyumbani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_14', 3, NULL, 'assets/topic/asb/5820.png', 'Wakati Mukhwana alimwambia babake kisa hicho,  babake aliona kwamba alikuwa amefanya kosa. Alimwita Namukhaywa, na pamoja walienda kwa mkunga kumleta Mulongo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_15', 3, NULL, 'assets/topic/asb/5822.png', 'Mulongo alipofika nyumbani, babake na wazee wa kijiji walimchinja mbuzi na kutekeleza kimila cha kumwunganisha Mulongo na ndugu zake saba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7874_16', 3, NULL, 'assets/topic/asb/5824.png', 'Mwaka mmoja baadaye, Mulongo aliolewa kwa mwana wa tajiri kutoka kijijini. Aliiletea familia yake furaha na mali mengi.', NULL);
