INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049', 2, 'Nani atahesabu mpaka kumi?', 'assets/topic/asb/25319.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_3', 3, NULL, 'assets/topic/asb/25317.png', 'Hapo zamani, Mfalme Chui alianza kuwaza juu ya maisha ya usoni.  Alitafakari, "Ninazeeka, na siku moja, nitakufa. Mfalme nwenye busara, anastahili kumchagua mrithi wake akiwa angali kijana na mwenye afya."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_4', 3, NULL, 'assets/topic/asb/25318.png', 'Mfalme Chui alipata wazo. Aliwaalika wanyama wote kwenda katika ikulu. Wajumbe walikimbia kila sehemu ya kichaka wakiwaalika wanyama.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_5', 3, NULL, 'assets/topic/asb/25319.png', 'Usiku wa sherehe, wanyama wote walikuwa katika ikulu. Waliimba, wakacheza  wakawa na wakati mzuri. Usiku wa manane, Mfalme Chui alisimama katikati ya ikulu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_6', 3, NULL, 'assets/topic/asb/25320.png', 'Alisafisha koo akasema, "Nilikuwa nikiwaza na wakati umetimia wa kumchagua mrithi wangu. Kwa sababu ninawapenda nyote, siwezi kuamua nani anayefaa zaidi. Nimepanga mashindano yatakayoamua."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_7', 3, NULL, 'assets/topic/asb/25321.png', 'Mfalme Chui alishika mkuki akasema, "Wa kwanza miongoni mwenu atakayerusha mkuki angani kisha ahesabu mpaka kumi kabla ya mkuki huo kutua chini, ndiye atakayekuwa mrithi wangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_8', 3, NULL, 'assets/topic/asb/25322.png', 'Mfalme Chui alipomaliza kuzungumza, wanyama walisikia sauti kubwa ikitoka nyuma yao. Walipogeuka, walimwona Tembo akielekea mbele kwa kishindo. "Tokeni njiani. Mimi ni mkubwa zaidi, ninastahili kuwa mfalme," Tembo alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_9', 3, NULL, 'assets/topic/asb/25323.png', 'Tembo aliuchukua mkuki, akaushika kwa mkonga wake. Alikirudisha kichwa chake nyuma kisha akaurusha mkuki angani. "Moja! Mbili! Tatu! Ah!" Tembo alilia. Mkuki uligonga chini alipofikisha nne. Tembo alikasirika akaanza kufoka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_10', 3, NULL, 'assets/topic/asb/25324.png', 'Baada ya Tembo kuondoka, wanyama walianza kuzungumza miongoni mwao kwa msisimuko. Walisikia tena sauti kubwa ikitoka nyuma yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_11', 3, NULL, 'assets/topic/asb/25325.png', 'Ngiri aliinama, akaukamata mkuki kisha akaurusha angani. "Moja! Mbili! Tatu! Nne! Tano! Ah!" Alipiga kelele. Mkuki ulianguka alipofikisha sita. Alirusha uchafu hewani kwa hasira.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_12', 3, NULL, 'assets/topic/asb/25326.png', 'Baadaye, wanyama walianza kutoa maoni yao na kusema, "Mashindano hayo ni magumu! Tembo, ingawa ni mkubwa, hakufaulu. Ngiri, ingawa ana nguvu sana, pia hakufaulu. Hakuna atakayeshinda mashindano haya!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_13', 3, NULL, 'assets/topic/asb/25327.png', 'Walimwona Sokwe mkubwa akienda mbele. Alikuwa akiimba, "Ninaweza. Najua ninaweza. Ninaweza." Sokwe aliuchukua mkuki akasonga nyuma hatua kadhaa. Aliuvuta mkono wake nyuma, akarudi mbele kisha akaruka hewani na kuurusha mkuki angani. "Moja! Mbili! Tatu! Nne! Tano! Sita! Saba! Ah!" Sokwe alilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_14', 3, NULL, 'assets/topic/asb/25328.png', 'Mkuki uligonga chini alipofikisha nane. Sokwe alikasirika akazungukazunguka akilalamika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_15', 3, NULL, 'assets/topic/asb/25329.png', 'Baadhi ya wanyama walianza kuondoka kwenda nyumbani. Walipokuwa wakiondoka, walimwona Swara mdogo akija kujaribu bahati yake. Swara alisema, "Subirini! Subirini! Hebu nijaribu. Ninaweza kufaulu. Ninaweza. Nipeni nafasi nijaribu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_16', 3, NULL, 'assets/topic/asb/25330.png', 'Mfalme Chui aliwashauri wanyama, "Acheni kumcheka Swara! Ni nani aliyesema kwamba wanyama wadogo hawawezi kufanya kazi ambayo wale wakubwa wanafanya? Lazima Swara apewe nafasi sawa kama wanyama wengine. Rudini nyuma ili Swara atupe mkuki."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_17', 3, NULL, 'assets/topic/asb/25331.png', 'Swara alimwinamia mfalme, akauchukua mkuki mdomoni. Akitumia nguvu zote, alikimbia mpaka katikati ya uwanja. Aliruka juu akavuta pumzi kisha akaurusha mkuki huku akisema kwa sauti kubwa, "Tano na tano ni kumi." Mkuki ulitua chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_18', 3, NULL, 'assets/topic/asb/25332.png', 'Wanyama wote walichanganyikiwa. Mfalme Chui aliwaeleza, "Ndiyo, Swara! Tano na tano ni njia nyingine ya kupata kumi. Kuna zaidi ya njia moja  ya kuhesabu mpaka kumi." Mashindano hayakuwa kumpata mnyama mkubwa wala mwenye nguvu. Bali lilikuwa kumpata mnyama mwerevu! Hivyo ndivyo Swara alivyokuwa malkia baada ya chui kufariki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22049_19', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747', 2, 'Naweza kufanya hili!', 'assets/topic/asb/13814.png', 'HANNINGTON  OWERE
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_4', 3, NULL, NULL, 'Amelia anamka na anadhani, "Leo ni siku yangu ya kwanza ya shule."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_6', 3, NULL, NULL, '"Carlos, ninafanya nini?" Amelia anauliza. "Nitakwambia," Carlos anajibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_8', 3, NULL, NULL, 'Carlos anamwambia Amelia kuosha uso wake. Amelia anasema, "Ninaweza kuosha uso wangu. Na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_10', 3, NULL, NULL, 'Carlos anamwambia Amelia kupiga meno yake. Amelia anasema, "Ninaweza kupiga meno yangu na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_12', 3, NULL, NULL, 'Carlos amwambia Amelia kwamba lazima ale. Amelia anasema, "Ninajua jinsi ya kula, na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_14', 3, NULL, NULL, 'Carlos anamwambia Amelia kuvaa sare yake. Amelia anasema, "Ninaweza kuvaa sare yangu. Na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_16', 3, NULL, NULL, 'Carlos anamwambia Amelia kuchukua mfuko wake. Amelia anasema, "Ninaweza kuchukua mfuko wangu na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_18', 3, NULL, NULL, 'Carlos anasema, "Lazima uende shuleni." Amelia anasema, "Ninaweza kwenda shule, na kisha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23747_20', 3, NULL, NULL, 'Carlos anasema, "Lazima uingie darasani." Amelia anajibu, "Ninaweza kuingia darasa na kisha?"', NULL);
