INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747', 2, 'Ndama wa Parna', 'assets/topic/asb/25507.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_3', 3, NULL, 'assets/topic/asb/25498.png', 'Parna alikuwa mvulana mwembamba mrefu. Alikuwa katika darasa la tatu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_4', 3, NULL, 'assets/topic/asb/25499.png', 'Wakati wa likizo, Parna aliwachunga ng''ombe wa wazazi wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_5', 3, NULL, 'assets/topic/asb/25500.png', 'Siku moja, Parna alikuwa akiwachunga ng''ombe. Ghafla, mvua ilianza kunyesha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_6', 3, NULL, 'assets/topic/asb/25501.png', 'Wakati huo huo, Cheusi, ng''ombe aliyekuwa kipenzi cha babake, alizaa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_7', 3, NULL, 'assets/topic/asb/25502.png', 'Parna alifurahi kuwahudumia Cheusi na ndama wake. Aliwasahau ng''ombe wale wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_8', 3, NULL, 'assets/topic/asb/25503.png', 'Aliwaza, "Babangu aliahidi kunipa ndama ikiwa atakuwa wa kike."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_9', 3, NULL, 'assets/topic/asb/25504.png', 'Alipokuwa akiwaza hivyo, alisikia mkono ukimvuta juu kwa nguvu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_10', 3, NULL, 'assets/topic/asb/25505.png', 'Mjomba Teteyo alimjibu, "Mamako aliwapata ng''ombe wote zizini ila wewe na Cheusi. Aliingiwa na wasiwasi akanituma nije nikuchukue."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_11', 3, NULL, 'assets/topic/asb/25506.png', 'Mjomba Teteyo akasema, "Nitambeba ndama huyu. Kisha wewe na Cheusi mnifuate nyuma."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_12', 3, NULL, 'assets/topic/asb/25507.png', 'Watu wote walifurahi walipoona kwamba Parna, Cheusi na ndama walikuwa salama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_13', 3, NULL, 'assets/topic/asb/25508.png', 'Parna akamwuliza babake, "Je, utanipa ndama huyu kama ulivyoahidi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21747_14', 3, NULL, 'assets/topic/asb/25509.png', '"Ndiyo, mwanangu, ndama huyu ni wako. Ahadi ni deni."', NULL);
