INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942', 2, 'Ndizi za Bibi', 'assets/topic/asb/1360.png', 'Peter Chege
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_3', 3, NULL, 'assets/topic/asb/1361.png', 'Bibi alikuwa na shamba lenye rutuba lililojaa mtama,  mawele, mihogo, na zaidi ya yote, ndizi. Ingawa bibi alikuwa na wajukuu wengi, nilijua alinipenda zaidi. Alinikaribisha nyumbani kwake mara kwa mara na kunifichulia siri zake. Kulikuwa na siri moja aliyokataa kunifichulia. Hakunifichuliwa alivyoziivisha ndizi zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_4', 3, NULL, 'assets/topic/asb/1360.png', 'Siku moja, niliona kapu kubwa limeanikwa nje ya nyumba ya bibi. Nilipomuuliza kapu lilikuwa la nini,  alinijibu, "Hili ni kapu langu la ajabu." Kando ya kapu lile,  paliwekwa majani ya mgomba. Bibi alikuwa akiyageuzageuza. Nilijawa na shauku nikauliza,  "Bibi, majani haya ni ya nini?" Bibi alinjibu, "Ni majani yangu ya ajabu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_5', 3, NULL, 'assets/topic/asb/1362.png', 'Nilifurahia kumtazama bibi, zile ndizi, yale majani ya mgomba na lile kapu kubwa. Bibi alinituma kwa mamangu. "Bibi, tafadhali niruhusu nione unavyotayarisha." "Mwana wee, usiwe mtundu,  fanya ninavyokwambia." Bibi alinifokea. Niliondoka mbio.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_6', 3, NULL, 'assets/topic/asb/1363.png', 'Niliporudi nilimpata bibi amekaa nje. Hapakuwa na kapu wala zile ndizi. "Bibi, li wapi kapu, zi wapi ndizi,  na ya wapi." "Zipo mahali pangu pa ajabu." Bibi alijibu. Jibu lake lilinivunja moyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_7', 3, NULL, 'assets/topic/asb/1364.png', 'Siku mbili baadaye, bibi alinituma nimchukulie mkongojo wake kutoka chumbani. Mara tu nilipoufungua mlango, nilikaribishwa na harufu tamu ya ndizi mbivu. Kapu kubwa la ajabu la bibi lilikuwa kwenye chumba cha ndani. Lilifunikwa kwa blanketi kuukuu. Nililifunua blanketi na kunukia harufu tamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_8', 3, NULL, 'assets/topic/asb/1366.png', 'Nilibumburushwa na sauti ya bibi akiita, "Wafanyaje wewe? Niletee mkongojo haraka." Nilitoka haraka kumpelekea mkongojo. "Tabasamu ni la nini?" Aliniuliza bibi. Swali lake lilinikumbusha kuwa bado nilikuwa ninatabasamu kwa kupavumbua mahali pake pa ajabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_9', 3, NULL, 'assets/topic/asb/1368.png', 'Keshoye bibi alipokuja kumuamkua mama,  nilichapuka na kwenda nyumbani kwa bibi kuziangalia zile ndizi tena. Palikuwa na kichala kimoja cha ndizi zilizoiva. Nilichuna moja na kuificha rindani mwangu. Nililifunika kapu, nikaenda nyuma ya nyumba, nikaila ndizi kwa haraka. Ilikuwa tamu ajabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_10', 3, NULL, 'assets/topic/asb/1369.png', 'Siku iliyofuata, bibi alipokuwa kitaluni akichuma mboga, nilipenya haraka nikazichungulia ndizi. Karibu ndizi zote zilikuwa zimeiva. Nikakichukuwa kichala cha ndizi nne. Nilipokuwa nikinyemelea kuelekea mlangoni, nilimsikia bibi akikohoa. Niliwahi kuzificha rindani na nikampita nikelekea nje.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_11', 3, NULL, 'assets/topic/asb/1370.png', 'Siku iliyofuata, ilikuwa siku ya soko. Bibi aliamka alfajiri mapema. Kila mara aliziuza ndizi na mihogo sokoni. Siku hiyo, sikuharakisha kumtembelea. Ingawa hivyo, singeweza kumuepuka kwa muda mrefu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15942_12', 3, NULL, 'assets/topic/asb/1371.png', 'Siku hiyo jioni, baba, mama na bibi waliinita. Nilijua sababu. Usiku ule nilipokuwa nimelala, nilijua singewahi kuiba tena, si kwa bibi, si kwa wazazi wangu, wala kwa mtu yeyote.', NULL);
