INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877', 2, 'Ndogo sana, Kubwa sana', 'assets/topic/asb/2282.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_3', 3, NULL, 'assets/topic/asb/2283.png', 'Nelima aliita, "Mama! Njoo uone. Nguo hizi zote ni ndogo kwangu!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_4', 3, NULL, 'assets/topic/asb/2284.png', '"Iangalie hii sketi yangu. Ni ndogo mno," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_5', 3, NULL, 'assets/topic/asb/2285.png', '"Tazama suruali yangu. Ni ndogo sana," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_6', 3, NULL, 'assets/topic/asb/2286.png', '"Mama, angalia fulana yangu. Ni ndogo sana," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_7', 3, NULL, 'assets/topic/asb/2287.png', '"Angalia sweta yangu. Ni ndogo sana," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_8', 3, NULL, 'assets/topic/asb/2288.png', '"Angalia koti langu la mvua. Ni dogo sana," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_9', 3, NULL, 'assets/topic/asb/2289.png', '"Angalia soksi zangu. Ni ndogo sana," Nelima akasema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_10', 3, NULL, 'assets/topic/asb/2290.png', '"Angalia viatu vyangu. Ni vidogo mno," Nelima alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7877_11', 3, NULL, 'assets/topic/asb/2291.png', '"Sasa umepata nguo nyingi, sana," Nelima akamwambia Sela.
