INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684', 2, 'Ndoto ya Graca', 'assets/topic/asb/8850.png', 'Rebbeccah Karoki
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_3', 3, NULL, 'assets/topic/asb/8852.png', 'Graca alizaliwa Msumbiji. Nchi yake ilikuwa maskini sana na hawakuwa wamepata uhuru. Watoto hawakuwa na shule nzuri za kwenda. Mamake alikuwa na ndoto ya kuwapa wanawe maisha mazuri lakini bila masomo haingewezekana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_4', 3, NULL, 'assets/topic/asb/8853.png', 'Mtoto Graca angekuwa mzaliwa wa sita. Licha ya furaha hii yote kulikuwa na huzuni kwani babake Graca alikuwa karibu kufa. Aliota kuwa mtoto wake mdogo angeenda shule nzuri. Alijua elimu ndiyo ingemsaidia mwanawe. Jamii yake iliapa kutimiza ndoto yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_5', 3, NULL, 'assets/topic/asb/8854.png', 'Wiki chache baada ya kifo cha babake, mtoto alipewa jina kuambatana na urembo wake. Aliitwa Graca. Kadri miaka ilipozidi kuyoyoma ndivyo Graca alizidi kuleta furaha kwao. Familia iliamua kumpa Graca masomo mazuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_6', 3, NULL, 'assets/topic/asb/8855.png', 'Graca alitia bidii sana shuleni. Alipata tuzo ambalo lilibadilisha maisha yake. Alipata ufadhili wa kumwezesha kusomea shule nzuri kijijni. Ndoto yake ilikuwa awe mwalimu. Alitamani watoto wa Msumbiji wajue kusoma na kuandika. Alitamani wakati ambapo watoto wote wangeenda shuleni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_7', 3, NULL, 'assets/topic/asb/8856.png', 'Graca alitia bidii yake yote kwenye masomo. Baadaye kidogo alipata tuzo nyingine la kuhudhuria chuo kikuu huko Ureno. Alipata marafiki wengi,  akajifunza lugha nyingi geni na akasoma vitabu vingi vipya. Akaona ndoto yake ya kuwa mwalimu ikitimia. Hii ilimpa furaha kubwa lakini bado alikuwa na huzuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_8', 3, NULL, 'assets/topic/asb/8857.png', 'Watu kule nyumbani kwao, hawakuwa na uhuru. Lakini sasa Graca alikuwa na elimu, ujuzi na matumaini. Pia, alikuwa na marafiki ambao walikuwa na mawazo kama yake kuhusu haki ya watoto kupata elimu. Alitaka kutumia ujuzi wake na marafiki zake kuleta mabadiliko kwao Msumbiji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_9', 3, NULL, 'assets/topic/asb/8858.png', 'Kupitia usaidizi wa marafiki zake, hatimaye Msumbiji ilikuwa huru!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_10', 3, NULL, 'assets/topic/asb/8859.png', 'Rais aliyechaguliwa kuiongoza nchi ya Msumbiji alikuwa rafiki yake Samora. Walipendana na wakaona.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_11', 3, NULL, 'assets/topic/asb/8860.png', 'Graca alipewa kazi ya kuhakikisha kuwa watoto wote wamepata elimu nzuri. Ilikuwa kazi ngumu kwani watoto wengi wa nchini Msumbiji hawakujua kusoma na kuandika. Alianza kwa kuwapeleka watoto wote shuleni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_12', 3, NULL, 'assets/topic/asb/8861.png', 'Graca na Samora walijaliwa kupata watoto wawili. Walikuwa na ndoto ya kuwapa watoto wao na watu wa Msumbiji maisha mazuri. Kwa bahati mbaya, Samora alikufa kwa ajali ya ndege.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9684_13', 3, NULL, 'assets/topic/asb/8862.png', 'Graca aliomboleza kifo cha Samora kwa miaka mingi. Baadaye Graca alipendana na Nelson Mandela. Walifunga ndoa, wakafanya kazi pamoja kuwasaidia watoto wa Afrika.', NULL);
