INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616', 2, 'Ndovu na Kinyonga', 'assets/topic/asb/9461.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_3', 3, NULL, 'assets/topic/asb/9457.png', 'Hapo zamani katika ikulu kubwa,  paliishi mfalme na bintiye mrembo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_4', 3, NULL, 'assets/topic/asb/9458.png', 'Ndovu aliishi katika nyumba upande mmoja wa ikulu. Kinyonga aliishi upande mwingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_5', 3, NULL, 'assets/topic/asb/9459.png', 'Wakati huo,  kukashuhudiwa kiangazi kikubwa katika nchi ile. Mfalme alikuwa na wazo kisha akawaita majirani zake kwenda ikuluni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_6', 3, NULL, 'assets/topic/asb/9460.png', 'Mfalme aliwaambia Ndovu na Kinyonga, "Ninataka mkanyage ardhi hadi maji yapatikane." Mfalme aliahidi kuwa ambaye angefaulu angemwoa bintiye wa kifalme.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_7', 3, NULL, 'assets/topic/asb/9461.png', 'Kinyonga hakuwa na matumaini yoyote kwa ajili ya udogo wake. Ndovu alifurahi mno kwani alikuwa na umbo kubwa na mwenye nguvu. Ndovu alienda uwanjani na kuanza kukanyaga aridhi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_8', 3, NULL, 'assets/topic/asb/9462.png', 'Vumbi nyingi ilitokea, lakini hapakuwa na maji. Ndovu alikanyaga aridhi mpaka karibu maji yatokee, lakini alielekea kuchoka. Alimwachia Kinyonga nafasi kujaribu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_9', 3, NULL, 'assets/topic/asb/9463.png', 'Kinyonga alianza kukanyaga aridhi. Baada ya muda mfupi,  maji yalitoka. Watu hawakuamini macho yao!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15616_10', 3, NULL, 'assets/topic/asb/9464.png', 'Kwa hivyo, Mfalme alimpa Kinyonga bintiye. Ndovu alienda nyumbani kwa hasira.', NULL);
