INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480', 2, 'Ndovu na Vyura', 'assets/topic/asb/8197.png', 'Caren Echesa
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_3', 3, NULL, 'assets/topic/asb/8195.png', 'Hapo kale, mfalme wa mwitu alikuwa Bwana Simba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_4', 3, NULL, 'assets/topic/asb/8196.png', 'Aliwatuma wanyama kukomesha kelele zilizofanywa na vyura. Wanyama hao hawakufaulu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_5', 3, NULL, 'assets/topic/asb/8197.png', 'Ndovu alijitolea kukomesha. Alitumwa nje kuwa mlinzi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_6', 3, NULL, 'assets/topic/asb/8198.png', 'Usiku wa manane kelele ziliposikika, ndovu alianza kuwaua vyura. Alifanya hivyo siku iliyofuata.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_7', 3, NULL, 'assets/topic/asb/8199.png', 'Baadaye, Mfalme Simba alikuwa mwenye furaha kwa kuwa ndovu alikuwa amezikomesha zile kelele.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_8', 3, NULL, 'assets/topic/asb/8200.png', 'Baada ya mwezi mmoja, waliwaona wadudu wengi shambani mwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_9', 3, NULL, 'assets/topic/asb/8201.png', 'Mfalme Simba alimwuliza ndovu kuwaita vyura waende kuwala wadudu wale.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_10', 3, NULL, 'assets/topic/asb/8202.png', 'Ndovu alijawa na hofu. Alikuwa amewaua vyura wote. Alisema, "Samahani, samahani, ni-li-waua wote!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_11', 3, NULL, 'assets/topic/asb/8203.png', 'Mfalme Simba alimwambia Ndovu, "Nilikutuma kukomesha kelele, wala sio kuwaua vyura." Ndovu aliwekwa gerezani akakaa kwa miaka mingi!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9480_12', 3, NULL, 'assets/topic/asb/8195.png', 'Mfalme aliwaambia wanyama wote wafanye alilosema kila wakati.', NULL);
