INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621', 2, 'Ndugu mwerevu', 'assets/topic/asb/19342.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_3', 3, NULL, 'assets/topic/asb/19349.png', 'Hapo zamani za kale, kulikuwa na mtu aliyekuwa na watoto wa kiume watatu. Mtoto wa mwisho alikuwa mwerevu zaidi kuliko wote na babake alimpenda zaidi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_4', 3, NULL, 'assets/topic/asb/19340.png', 'Siku moja kaka wakubwa walimwambia ndugu mdogo, "Tutamchinja fahali wako." Kwa vile alikuwa mdogo, alisema, "Ikiwa ni lazima mfanye hivyo, siwezi kuwazuia. Lakini, tafadhali, nipeni ngozi ya fahali wangu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_5', 3, NULL, 'assets/topic/asb/19351.png', 'Giza lilipoingia, wafanya biashara walienda kulala chini ya mti ule. Usiku wa manane ndugu yule aliipiga ngozi ile kwa fimbo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_6', 3, NULL, 'assets/topic/asb/19345.png', 'Ndugu mdogo alizichukua bidhaa zile na kuzipeleka nyumbani kwa kaka zake wakubwa. Walimwuliza, "Ulipata wapi bidhaa hizi zote?" Ndugu yule alisema, "Siku hizi bei ya ngozi ni ghali sana. Nimevipata vitu hivi vyote kwa kuuza ngozi moja tu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_7', 3, NULL, 'assets/topic/asb/19348.png', 'Kaka wale waliwachinja haraka ng''ombe wao wote kisha wakaenda sokoni wakiita, "Tunauza ngozi! Tunauza ngozi!" Watu walisema, "Hatutaki ngozi zenu!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_8', 3, NULL, 'assets/topic/asb/19342.png', 'Ndugu yule mwerevu alisema, "Siwezi kuwazuia lakini, tafadhali, nipeni jivu." Walikiteketeza chumba chake. Ndugu mdogo aliliweka jivu mfukoni akaenda nalo kwa tajiri mmoja. Tajiri alimruhusu kulala nyumbani kwake. Alipoenda kupata chakula, alimwomba tajiri, "Tafadhali nilindie mfuko huu. Kunamo vitu vingi vya thamani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_9', 3, NULL, 'assets/topic/asb/19341.png', 'Asubuhi, yule ndugu mwerevu alipiga mayowe akisema, "Watu wameiba bidhaa zangu zote za thamani na badala yake wameujaza mfuko na jivu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_10', 3, NULL, 'assets/topic/asb/19347.png', 'Ndugu yule alirudi nyumbani akasema, "Tazameni!  Jivu limekuwa na bei ghali mno siku hizi. Hivi ndivyo vitu nilivyoweza kununua."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_11', 3, NULL, 'assets/topic/asb/19344.png', 'Kaka wakubwa walikasirika wakaamua kumwua ndugu yao mdogo. Walimtia kikapuni ili wamtupe chini kutoka kwenye jabali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_12', 3, NULL, 'assets/topic/asb/19350.png', 'Ndugu yule mdogo alimwita mzee kutoka kikapuni.  Mzee alimwuliza, "Je, una shida gani?" Ndugu mdogo alisema, "Ndugu zangu wanataka niwe mfalme lakini miye sitaki kuwa mfalme."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_13', 3, NULL, 'assets/topic/asb/19343.png', 'Mzee alimtoa ndugu mdogo kikapuni naye akaingia ndani. Kaka wakubwa waliporudi na ng''ombe walikichukua kikapu na kukitosa chini ya jabali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21621_14', 3, NULL, 'assets/topic/asb/19346.png', 'Ndugu mdogo akawajibu, "Kuna ng''ombe wengi chini ya jabali. Ninyi pia mkitoswa kule chini mkiwa vikapuni, vilevile mtawapata ng''ombe."
