INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687', 2, 'Nelima atajirika', 'assets/topic/asb/9239.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_3', 3, NULL, 'assets/topic/asb/9230.png', 'Hapo zamani za kale, kulikuwa na yatima wawili,  Nelima na Nambuya. Waliishi katika shamba lisilokuwa na rutuba. Kwa muda mrefu hakukunyesha na hawakuwa na chakula. Walilazimika kwenda mbali kutafuta chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_4', 3, NULL, 'assets/topic/asb/9231.png', 'Nelima alikuwa mnyamavu, mnyenyekevu na alipenda kusaidia. Dadake Nambuya alikuwa mwenye kiburi na hakumsikiza yeyote. Hakuwajali watu wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_5', 3, NULL, 'assets/topic/asb/9232.png', 'Siku moja, Nelima na Nambuya walipoamka,  walitambua kwamba hawakuwa na chochote cha kula. Walilazimika kutafuta chakula. Kila mmoja alikwenda kutafuta upande tofauti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_6', 3, NULL, 'assets/topic/asb/9233.png', 'Wakati huo huo, aliishi Bi Kizee mmoja aliyeitwa Netasile. Netasile alikuwa na ugonjwa wa ukoma na mwili wake ulijaa vidonda. Watu wa kijiji chake walikuwa na imani kuwa angeweza kumpa mtu utajiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_7', 3, NULL, 'assets/topic/asb/9234.png', 'Alipokwenda kutafuta chakula, Nambuya alikutana na Bi Kizee Netasile. Netasile akamwita, "Mwanangu, u hali gani? Unatoka wapi? Unakwenda wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_8', 3, NULL, 'assets/topic/asb/9235.png', 'Nambuya alijibu maswali yote kwa dharau. Netasile hakusita, akasema, "Nibebe unipeleke nyumbani kwangu nami nitakwambia la kufanya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_9', 3, NULL, 'assets/topic/asb/9237.png', 'Nambuya akasema kwa kiburi, "Heri nife kuliko kuvigusa hivyo vidonda vyako." Bi Kizee alipuuza maneno hayo akamtakia safari njema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_10', 3, NULL, 'assets/topic/asb/9236.png', 'Nambuya aliendelea kutembea. Alifika mahali palipokuwa na majongoo, nyoka, mabuu na wadudu wengine. Alikuwa amechoka na kudhoofika kwa kukosa chakula. Alipoketi chini kupumzika, wadudu walianza kumuuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_11', 3, NULL, 'assets/topic/asb/9238.png', 'Nelima naye alikutana na Netasile. Alifurahi kwani Netasile alikuwa binadamu wa kwanza aliyekutana naye tangu aanze safari yake. Netasile akamwita na kumuuliza, "Mwanangu, u hali gani? Unatoka wapi? Unakwenda wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_12', 3, NULL, 'assets/topic/asb/9239.png', 'Nelima alijibu kwa upole. Netasile akasema, "Nipeleke nyumbani nami nitakwambia la kufanya." Nelima alimpeleka. Netasile akasema, "Umenitendea lile wengi wemeshindwa kufanya." Alimpa Nelima kijiti ambacho kingemsaidia kupata vyote alivyotaka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_13', 3, NULL, 'assets/topic/asb/9240.png', 'Nelima alimshukuru Bi Kizee na kuelekea nyumbani. Alipofika nyumbani, alipata nyumba mpya iliyojaa vitu aina aina na chakula kingi. Tangu siku hiyo,  Nelima akawa tajiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12687_14', 3, NULL, 'assets/topic/asb/9241.png', 'Nelima hakufurahia kumpoteza dadake lakini alisema, "Asiyesikia ushauri huishia ndani ya kinywa cha ndovu."', NULL);
