INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712', 2, 'Ng''ombe aliye na pembe moja', 'assets/topic/asb/9612.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_3', 3, NULL, 'assets/topic/asb/9613.png', 'Haaaa! Nyumbani kwetu, kuna ng''ombe wa ajabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_4', 3, NULL, 'assets/topic/asb/9614.png', 'Ana pembe moja tu na hana mkia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_5', 3, NULL, 'assets/topic/asb/9615.png', 'Ni ng''ombe mnono aliye na tumbo kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_6', 3, NULL, 'assets/topic/asb/9616.png', 'Ni ng''ombe aliye na nguvu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_7', 3, NULL, 'assets/topic/asb/9617.png', 'Mchungaji anampenda ng''ombe huyu sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_8', 3, NULL, 'assets/topic/asb/9618.png', 'Anapokuwa malishoni, ng''ombe huyu hujitenga na ng''ombe wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_9', 3, NULL, 'assets/topic/asb/9619.png', 'Ni ng''ombe aliye hodari katika vita.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15712_10', 3, NULL, 'assets/topic/asb/9620.png', 'Watu wengi wanamjua ng''ombe huyu. Wanamwogopa kwa sababu yeye hupigana kwa pembe yake moja.
