INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24042', 2, 'Ng''ombe ni rafiki Yangu', 'assets/topic/asb/6379.png', 'Emma Nuella
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24042_3', 3, NULL, 'assets/topic/asb/6373.png', 'ng''ombe ng''ombe ng''ombe, Mimi Kama wewe ng''ombe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24042_4', 3, NULL, 'assets/topic/asb/6376.png', 'Jana umenipa maziwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24042_5', 3, NULL, NULL, 'Baba yangu anununua nyama kila jumamosi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24042_6', 3, NULL, 'assets/topic/asb/6105.png', 'Mama ina mfuko mzuri,na nina nzuri sana.', NULL);
