INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003', 2, 'Ng''ombe wa Sokoni', 'assets/topic/asb/1607.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_3', 3, NULL, 'assets/topic/asb/1608.png', 'Watu kijijini walijua kwamba Mama Schola alipenda kupiga kelele mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_4', 3, NULL, 'assets/topic/asb/1609.png', 'Siku hiyo, alifanya hivyo kuhusiana na ng''ombe waliokuwa sokoni. "Wafukuze waende mbali hawa ng''ombe wajinga," alisema. "Wanakula mboga zangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_5', 3, NULL, 'assets/topic/asb/1609.png', '"Hei we pale! Wafukuze hawa ng''ombe wajinga watoke hapa," alisisitiza. "Wanakula nguo zangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_6', 3, NULL, 'assets/topic/asb/1610.png', '"Wapi mwenye ng''ombe hawa wajinga?" alipiga kelele. "Wanakula nafaka yangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_7', 3, NULL, 'assets/topic/asb/1611.png', 'Kutoka mafichoni, Sorimpan, mchungaji,  alichuchumaa kwa kumuogopa Mama Schola. Alikuwa ameenda kunywa maji wakati ng''ombe wake waliranda na kwenda sokoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_8', 3, NULL, 'assets/topic/asb/1612.png', '"Sasa tazama walichofanya hawa ng''mbe wajinga!" Aliendelea kupiga kelele. "Wamevivunja vyungu vyangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_9', 3, NULL, 'assets/topic/asb/1613.png', 'Baadaye kidogo, Mama Schola alisema, "Aah! Sio tena! Nitashtaki jambo hili kwa polisi. Hawa ng''ombe wajinga wamekiangusha kibanda changu cha matunda."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_10', 3, NULL, 'assets/topic/asb/1614.png', 'Sorimpan, fimbo mkononi, alihepa kati ya umati akfaulu kuwaona ng''ombe wake. Ng''ombe wa Sorimpan walimjua vyema. Waligeuka na kumwangalia mara tu alipotokea!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_11', 3, NULL, 'assets/topic/asb/1617.png', 'Wakati huo, palikuwa na umati mkubwa wa watazamaji: wanaume, wanawake na hata watoto. Wote walisimama wakiangalia ng''ombe ambao walimtii Sorimapn pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_12', 3, NULL, 'assets/topic/asb/1607.png', 'Ghafla, ng''ombe walikivunja kimya chao! Wakaanza kuwakosoa watu! Watu walishangaa na kupiga hatua nyuma wakiwaogopa ng''ombe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_13', 3, NULL, 'assets/topic/asb/1608.png', 'Kisha ng''ombe waliacha kuzungumza na kuuangalia umati kwa mshangao! Mama Schola alisikika akisema, "Tangu lini ng''ombe wakaingilia shughuli zetu za sokoni?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_14', 3, NULL, 'assets/topic/asb/1617.png', 'Sasa ilikuwa zamu ya Mama Schola kukosolewa na ng''ombe. Wanawake wote waliokuwa wakiuza,  walisonga na kujiunga na Mama Schola kwa ajili ya woga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_15', 3, NULL, 'assets/topic/asb/1618.png', 'Ng''ombe waliwakosoa wanawake. Waliongea kuhusu hali ya mboga zao wakisema, "Hizi ni mboga za aina gani? Zitazame jinsi zimeanza kunyauka!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_16', 3, NULL, 'assets/topic/asb/1619.png', 'Ng''ombe waligeuka na kuangalia kibanda cha nguo kisha walisema, "Mnadai kwamba tunakula nguo zenu! Nguo gani? Hivi viraka vikuu kuu ndivyo nguo! Nani atavinunua?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_17', 3, NULL, 'assets/topic/asb/1620.png', 'Ng''ombe sasa walisongea kibanda kilichofuata. Watu wote waliwafuata. "Nafaka yenu imejaa wadudu. Nani anataka kuila hiyo! Tulikuwa tuanaonja tu!" Ng''ombe walisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3003_18', 3, NULL, 'assets/topic/asb/1621.png', 'Waliendelea, "Nawe Mama Schola, acha kupiga kelele. Vyungu hivi havina thamani yoyote. Vinavunjika upesi. Usitulaumu." Kisha ng''ombe waliondoka wakifuatwa na Sorimpan.', NULL);
