INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531', 2, 'Ngoma', 'assets/topic/asb/25948.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_3', 3, NULL, 'assets/topic/asb/25947.png', 'Ngoma imekuwa ala muhimu ya muziki katika jamii mbalimbali. Tunazipenda ngoma!
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_4', 3, NULL, 'assets/topic/asb/25948.png', 'Tunazicheza ngoma kwa kutumia kijiti, au kwa kutumia vidole vyetu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_5', 3, NULL, 'assets/topic/asb/25949.png', 'Ngoma huongeza ladha kwenye nyimbo na kuwafanya watu watake kucheza. Ngoma hufanya sherehe kuwa maridadi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_6', 3, NULL, 'assets/topic/asb/25950.png', 'Ngoma hulia kutangaza majonzi na kuwaita watu kunapotokea kifo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_7', 3, NULL, 'assets/topic/asb/25951.png', 'Ngoma hulia kuwaita watu kusafisha kisima, barabara, au kumjengea mtu mkongwe nyumba.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_8', 3, NULL, 'assets/topic/asb/25952.png', 'Ngoma hulia kuwaita watu wakutane kwa chifu wetu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22531_9', 3, NULL, 'assets/topic/asb/25953.png', 'Ngoma huwaita watu kwenda kanisani kwa maombi siku ya Jumapili, na kwenye siku zingine za maombi.
