INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_4', 3, NULL, 'assets/topic/asb/19044.png', 'Ngurumo alikuwa mamake Radi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_5', 3, NULL, 'assets/topic/asb/19046.png', 'Radi alikuwa mwenye hasira kali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_6', 3, NULL, 'assets/topic/asb/19047.png', 'Alipokasirika, aliharibu watu, nyumba na miti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_7', 3, NULL, 'assets/topic/asb/19048.png', 'Radi alisababisha uharibifu mkubwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_8', 3, NULL, 'assets/topic/asb/19050.png', 'Radi hakujali kelele za mamake.', NULL);
