INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_10', 3, NULL, 'assets/topic/asb/19052.png', 'Mfalme aliwafukuza Ngurumo na Radi wakaishi mbali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_11', 3, NULL, 'assets/topic/asb/19053.png', 'Radi alipokasirika, aliendelea kuchoma misitu na mimea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21141_12', 3, NULL, 'assets/topic/asb/19054.png', 'Watu walienda tena kwa mfalme kulalamika.', NULL);
