INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653', 2, 'Nguruwe Hawaruhusiwi', 'assets/topic/asb/6814.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_3', 3, NULL, 'assets/topic/asb/6805.png', 'Miaka mingi iliyopita, jamii ya wanyama wenye pembe iliamua kuandaa karamu kubwa milimani. Wanyama wote wenye pembe walialikwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_4', 3, NULL, 'assets/topic/asb/6806.png', 'Nguruwe aliposikia kuhusu karamu hiyo, aliwaza,  "Mimi sina pembe. Je, nitafanyaje ili nihudhurie karamu hiyo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_5', 3, NULL, 'assets/topic/asb/6807.png', 'Nguruwe aliamua lazima angeshiriki katika ile karamu. Alisonga pembe kwa kutumia nta na kuzipachika kichwani mwake!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_6', 3, NULL, 'assets/topic/asb/6809.png', 'Nguruwe alifaulu na akakubaliwa kuhudhuria karamu hiyo. Karamu iliandaliwa juu milimani karibu na jua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_7', 3, NULL, 'assets/topic/asb/6808.png', 'Karamu ilipokuwa ikiendelea, joto lilizidi na nta ikaanza kuyeyuka. Mara pembe za nguruwe zikaanguka chini pu! Wanyama walipomwona nguruwe bila  pembe walishangaa na kuulizana, "Ni nani yule asiye na pembe? Kwa nini alialikwa?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_8', 3, NULL, 'assets/topic/asb/6810.png', 'Mfalme wa wanyama aliamuru nguruwe ainuliwe juu na kisha aangushwe. Walimnyanyua nguruwe na kumtupa chini kwenye bonde kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_9', 3, NULL, 'assets/topic/asb/6811.png', 'Maskini nguruwe, aliangukia pua lake. Papo hapo,  pua lake likabadilika na kuwa refu kama linavyoonekana hadi sasa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12653_10', 3, NULL, 'assets/topic/asb/6813.png', 'Usishangae unapoliona pua la nguruwe jinsi lilivyo.', NULL);
