INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433', 2, 'Ni kazi gani iliyo muhimu zaidi?', 'assets/topic/asb/10274.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_3', 3, NULL, 'assets/topic/asb/10274.png', 'Watu hawa wana maoni tofauti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_4', 3, NULL, 'assets/topic/asb/10278.png', '"Walimu ni muhimu zaidi. Mnatuhitaji sisi kujifunza," mwalimu anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_5', 3, NULL, 'assets/topic/asb/10276.png', '"Wajenzi ni muhimu. Mnahitaji nyumba," mjenzi anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_6', 3, NULL, 'assets/topic/asb/10279.png', '“Seremala ni muhimu zaidi. Mnahitaji samani.”', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_7', 3, NULL, 'assets/topic/asb/10277.png', '"Madaktari ni muhimu zaidi. Mnahitaji matibabu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_8', 3, NULL, 'assets/topic/asb/10275.png', '"Wakulima ni muhimu zaidi. Mnatuhitaji kwa chakula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_9', 3, NULL, 'assets/topic/asb/10280.png', '"Wanafunzi ni muhimu zaidi!" Mwanafunzi anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20433_10', 3, NULL, 'assets/topic/asb/10281.png', '"Nyote mlikuwa wanafunzi kwanza!"
