INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842', 2, 'Ni nani atakuwa mfalme?', 'assets/topic/asb/25319.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_3', 3, NULL, 'assets/topic/asb/25317.png', 'Mfalme Chui aliendelea kuwa mzee.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_4', 3, NULL, 'assets/topic/asb/25318.png', 'Mfalme  Chui aliwaita wanyama kwa sherehe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_5', 3, NULL, 'assets/topic/asb/25319.png', 'Baada ya sherehe, Mfalme Chui alizungumza.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_6', 3, NULL, 'assets/topic/asb/25320.png', 'Alisema, "Mashindano yataamua atakayekuwa mfalme."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_7', 3, NULL, 'assets/topic/asb/25321.png', 'Atakayehesabu hadi kumi kabla ya mkuki kuanguka, atakuwa mfalme.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_8', 3, NULL, 'assets/topic/asb/25322.png', 'Tembo aliwasukuma wengine, "Mimi ni mkubwa. Ninastahili kuwa mfalme."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_9', 3, NULL, 'assets/topic/asb/25323.png', 'Tembo alihesabu hadi nne pekee.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_10', 3, NULL, 'assets/topic/asb/25324.png', 'Ngiri aliwasukuma wenzake akisema, "Nina nguvu zaidi. Nitakuwa mfalme."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_11', 3, NULL, 'assets/topic/asb/25325.png', 'Ngiri alihesabu hadi sita kisha mkuki ulianguka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_12', 3, NULL, 'assets/topic/asb/25326.png', 'Wanyama walisema, "Tembo mkubwa na Ngiri wameshindwa. Nani atashinda?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_13', 3, NULL, 'assets/topic/asb/25327.png', 'Sokwe alihesabu, "Moja! Mbili! Tatu! Nne! Tano! Sita! Saba!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_14', 3, NULL, 'assets/topic/asb/25328.png', 'Mfalme Chui alisema, "Sokwe, unapate nafasi moja tu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_15', 3, NULL, 'assets/topic/asb/25329.png', 'Baadhi ya wanyama walianza kuondoka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_16', 3, NULL, 'assets/topic/asb/25330.png', 'Mfalme Chui aliwakemea, "Mpeni Swara nafasi. Mwache ajaribu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_17', 3, NULL, 'assets/topic/asb/25331.png', 'Swara alisema, "Tano na tano ni kumi."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21842_18', 3, NULL, 'assets/topic/asb/25332.png', 'Mashindano yalionyesha mnyama mwerevu.
