INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21836_7', 3, NULL, 'assets/topic/asb/1364.png', 'Bibi alinituma chumbani kwake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21836_8', 3, NULL, 'assets/topic/asb/1366.png', 'Nilikuwa nimejua pahali pa bibi pa miujiza.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21836_9', 3, NULL, 'assets/topic/asb/1368.png', 'Nilikula ndizi moja.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21836_10', 3, NULL, 'assets/topic/asb/1369.png', 'Nilichukua ndizi nne.
