INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106', 2, 'Ninafurahi / I Enjoy', 'assets/topic/asb/1492.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_3', 3, NULL, 'assets/topic/asb/1493.png', 'I enjoy skipping.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_4', 3, NULL, 'assets/topic/asb/1494.png', 'I enjoy skipping alone.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_5', 3, NULL, 'assets/topic/asb/1495.png', 'I enjoy skipping with my friend.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_6', 3, NULL, 'assets/topic/asb/1496.png', 'I enjoy reading good stories.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_7', 3, NULL, 'assets/topic/asb/1497.png', 'I enjoy walking with my friends.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_8', 3, NULL, 'assets/topic/asb/1498.png', 'I enjoy washing dishes for mother.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_9', 3, NULL, 'assets/topic/asb/1499.png', 'I enjoy writing letters that I can read easily.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_10', 3, NULL, 'assets/topic/asb/1500.png', 'I enjoy eating different fruits.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_11', 3, NULL, 'assets/topic/asb/1501.png', 'I enjoy sleeping on a big bed.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_12', 3, NULL, 'assets/topic/asb/1492.png', 'I enjoy hearing mother''s good stories.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_13', 3, NULL, 'assets/topic/asb/1502.png', 'I enjoy doing all these things!
