INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159', 2, 'Nozibele na Nywele Tatu', 'assets/topic/asb/1196.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_3', 3, NULL, 'assets/topic/asb/1197.png', 'Hapo zamani za kale,  wasichana watatu walienda kutafuta kuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_4', 3, NULL, 'assets/topic/asb/1198.png', 'Ilikuwa siku yenye joto kwa hivyo walienda mtoni kuogelea. Walicheza wakirushiania maji na kuogelea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_5', 3, NULL, 'assets/topic/asb/1196.png', 'Ghafla,  waligundua kuwa saa zilikuwa zimeenda. Waliharakisha kurudi kijijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_6', 3, NULL, 'assets/topic/asb/1199.png', 'Walipokuwa karibu nyumbani,  Nozibele aliweka mkono wake shingoni. Alikuwa amesahau mkufu wake! "Tafadhali rudini nami!" aliwasihi marafiki zake. Lakini,  marafiki zake walisema kwamba saa zilikuwa zimeenda sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_7', 3, NULL, 'assets/topic/asb/1200.png', 'Kwa hivyo Nozibele alirudi mtoni peke yake. Aliupata mkufu wake na kuharakisha kurudi nyumbani. Lakini,  alipotea gizani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_8', 3, NULL, 'assets/topic/asb/1201.png', 'Kwa umbali aliona mwangaza ukitokezea chumbani. Aliharakisha kuelekea chumba hicho na kubisha hodi mlangoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_9', 3, NULL, 'assets/topic/asb/1202.png', 'La kushangaza ni kuwa mbwa ndiye aliufungua mlango na kusema, "Unataka nini?" "Nimepotea,  nahitaji mahali pa kulala," Nozibele alisema. "Ingia ndani, au nikuume!" mbwa akajibu. Nozibele aliingia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_10', 3, NULL, 'assets/topic/asb/1203.png', 'Kisha mbwa akasema, "Nipikie chakula!" "Lakini sijawahi kumpikia mbwa chakula," Nozibele aljibu. "Pika, au nikuume!" mbwa akasema. Nozibele alimpikia mbwa chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_11', 3, NULL, 'assets/topic/asb/1204.png', 'Kisha mbwa akasema, "Nitayarishie kitanda." Nozibele akajibu, "Sijawahi kumtayarishia mbwa kitanda." "Tayarisha au nikuume!" mbwa alisema. Nozibele alitayarisha kitanda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_12', 3, NULL, 'assets/topic/asb/1207.png', 'Alitakiwa kumpikia, kumfagilia na kumwoshea mbwa kila siku. Kisha siku moja mbwa alisema, "Nozibele,  leo nalazimika kuwatembelea marafiki zangu. Fagia nyumba, pika chakula na uoshe vitu vyangu kabla sijarudi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_13', 3, NULL, 'assets/topic/asb/1208.png', 'Mbwa alipoenda tu, Nozibele alichukua nywele tatu kutoka kichwani kwake. Aliweka mmoja mvunguni mwa kitanda, mwingine nyuma ya mlango, na mwingine kwenye zizi la ng''ombe. Kisha alikimbia kwenda nyumbani haraka alivyoweza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_14', 3, NULL, 'assets/topic/asb/1209.png', 'Mbwa aliporudi, alimtafuta Nozibele. "Nozibele, uko wapi?" Aliita kwa sauti. "Niko hapa mvunguni mwa kitanda," unywele wa kwanza ulisema. "Niko hapa  nyuma ya mlango," unywele wa pili ukasema. "Niko hapa ndani kwenye zizi la ng''ombe," ukasema unywele wa tatu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_15', 3, NULL, 'assets/topic/asb/1210.png', 'Papo hapo mbwa alifahamu kuwa Nozibele alikuwa amemdanganya. Kwa hivyo, alikimbia moja kwa moja hadi kijijini. Lakini, ndugu zake Nozibele walikuwa wamemsubiri kwa fimbo kubwa. Mbwa aligeuka na kukimbia na hadi leo, hajawahi kuonekana tena.', NULL);
