INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994', 2, 'Nyani aliyekuwa chifu', 'assets/topic/asb/18900.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_3', 3, NULL, 'assets/topic/asb/18894.png', 'Don alikuwa nyani mkubwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_4', 3, NULL, 'assets/topic/asb/18895.png', 'Don ndiye alikuwa chifu wa nyani wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_5', 3, NULL, 'assets/topic/asb/18896.png', 'Wafuasi wake walimpatia kitambaa maalum.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_6', 3, NULL, 'assets/topic/asb/18897.png', '"Nitakivaa kichwani," Don alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_7', 3, NULL, 'assets/topic/asb/18898.png', '"Kila mmoja afuate masharti yangu. Msinihoji!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_8', 3, NULL, 'assets/topic/asb/18899.png', 'Wafuasi walimfuata chifu wao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_9', 3, NULL, 'assets/topic/asb/18900.png', 'Lakini, kile kitambaa, kilimkaza sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_10', 3, NULL, 'assets/topic/asb/18901.png', 'Don aligusa kichwa alikoumia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_11', 3, NULL, 'assets/topic/asb/18902.png', '"Hapa panuma," Don alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_12', 3, NULL, 'assets/topic/asb/18903.png', 'Alisema, "Hii imekazika sana!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_13', 3, NULL, 'assets/topic/asb/18904.png', 'Don alianguka kutoka juu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_14', 3, NULL, 'assets/topic/asb/18905.png', 'Nyani aliyekuwa chifu alikufa.
